import { useEffect, useState } from 'react';
import { useAuth } from '@/hooks/useAuth';
import { useToast } from '@/hooks/use-toast';
import { useQuery, useMutation } from '@tanstack/react-query';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Label } from '@/components/ui/label';
import { Users, FileText, CheckCircle2, Clock, Upload, Shield, MapPin, Activity } from 'lucide-react';
import type { User, Milestone, Report, Document } from '@shared/schema';

type Acknowledgement = {
  id: string;
  staffId: string;
  customerId: string;
  taskType: string;
  stage: number | null;
  status: 'pending' | 'acknowledged' | 'completed';
  acknowledgedAt: Date | null;
};

type StaffActivity = {
  id: number;
  staffId: string;
  customerId: string;
  actionType: string;
  stage: number | null;
  description: string;
  metadata: any;
  createdAt: Date;
};

type DeliveryLocation = {
  id: number;
  deliveryPersonId: string;
  latitude: number;
  longitude: number;
  status: string;
  updatedAt: Date;
};
import { isUnauthorizedError } from '@/lib/authUtils';
import { ObjectUploader } from '@/components/ObjectUploader';
import type { UploadResult } from '@uppy/core';

export default function Admin() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const [selectedCustomer, setSelectedCustomer] = useState<string>('');

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }

    if (!isLoading && user && user.role !== 'admin' && user.role !== 'clinical') {
      toast({
        title: "Access Denied",
        description: "You don't have permission to access this page.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, user, toast]);

  const isAuthorized = isAuthenticated && user && (user.role === 'admin' || user.role === 'clinical');

  const { data: customers, isLoading: loadingCustomers } = useQuery<User[]>({
    queryKey: ['/api/admin/customers'],
    enabled: isAuthorized,
  });

  const { data: milestones } = useQuery<Milestone[]>({
    queryKey: ['/api/admin/milestones', selectedCustomer],
    enabled: !!selectedCustomer && isAuthorized,
  });

  const { data: reports } = useQuery<Report[]>({
    queryKey: ['/api/admin/reports', selectedCustomer],
    enabled: !!selectedCustomer && isAuthorized,
  });

  const { data: documents } = useQuery<Document[]>({
    queryKey: ['/api/admin/documents', selectedCustomer],
    enabled: !!selectedCustomer && isAuthorized,
  });

  // New admin queries
  const { data: allAcknowledgements = [] } = useQuery<Acknowledgement[]>({
    queryKey: ['/api/admin/acknowledgements'],
    enabled: isAuthorized,
  });

  const { data: allActivities = [] } = useQuery<StaffActivity[]>({
    queryKey: ['/api/admin/activities'],
    enabled: isAuthorized,
  });

  const { data: deliveryLocations = [] } = useQuery<DeliveryLocation[]>({
    queryKey: ['/api/admin/delivery-locations'],
    enabled: isAuthorized,
  });

  const updateMilestoneMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      await apiRequest('PATCH', `/api/admin/milestones/${id}`, { status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/milestones', selectedCustomer] });
      toast({
        title: "Success",
        description: "Milestone updated successfully",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update milestone",
        variant: "destructive",
      });
    },
  });

  const createReportMutation = useMutation({
    mutationFn: async (report: { userId: string; reportType: string; title: string; summary: string }) => {
      await apiRequest('POST', '/api/admin/reports', report);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/reports', selectedCustomer] });
      toast({
        title: "Success",
        description: "Report created successfully",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create report",
        variant: "destructive",
      });
    },
  });

  const createDocumentMutation = useMutation({
    mutationFn: async (document: { userId: string; label: string; url: string; stage: number }) => {
      await apiRequest('POST', '/api/admin/documents', document);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/documents', selectedCustomer] });
      toast({
        title: "Success",
        description: "Document uploaded successfully",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to upload document",
        variant: "destructive",
      });
    },
  });

  if (isLoading || loadingCustomers || !isAuthorized) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-muted-foreground">Loading...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 py-8">
        <div className="mb-8">
          <h1 className="font-display text-3xl font-bold mb-2" data-testid="text-admin-title">
            Clinical Admin Panel
          </h1>
          <p className="text-muted-foreground">Manage customer milestones and reports</p>
        </div>

        <div className="grid gap-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Users className="h-5 w-5" />
                Select Customer
              </CardTitle>
            </CardHeader>
            <CardContent>
              <Select value={selectedCustomer} onValueChange={setSelectedCustomer}>
                <SelectTrigger data-testid="select-customer">
                  <SelectValue placeholder="Choose a customer" />
                </SelectTrigger>
                <SelectContent>
                  {customers?.map((customer) => (
                    <SelectItem key={customer.id} value={customer.id} data-testid={`select-customer-${customer.id}`}>
                      {customer.email} - {customer.firstName} {customer.lastName}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </CardContent>
          </Card>

          <Tabs defaultValue="staff" className="w-full">
            <TabsList className="grid w-full grid-cols-3 lg:grid-cols-6">
              <TabsTrigger value="staff" data-testid="tab-staff">
                Staff
              </TabsTrigger>
              <TabsTrigger value="acknowledgements" data-testid="tab-acknowledgements">
                Acknowledgements
              </TabsTrigger>
              <TabsTrigger value="delivery" data-testid="tab-delivery">
                Delivery
              </TabsTrigger>
              <TabsTrigger value="milestones" data-testid="tab-milestones">
                Milestones
              </TabsTrigger>
              <TabsTrigger value="documents" data-testid="tab-documents">
                Documents
              </TabsTrigger>
              <TabsTrigger value="reports" data-testid="tab-reports">
                Reports
              </TabsTrigger>
            </TabsList>

            <TabsContent value="staff" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Shield className="h-5 w-5" />
                    Staff Monitoring
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {['consultant', 'lab_technician', 'nutritionist', 'chef', 'delivery'].map(role => {
                      // Filter activities for this specific role
                      const roleActivities = allActivities.filter(act => 
                        (role === 'consultant' && act.actionType === 'document_uploaded' && (act.stage === 1 || act.stage === 3)) ||
                        (role === 'lab_technician' && act.actionType === 'document_uploaded' && act.stage === 2) ||
                        (role === 'nutritionist' && act.actionType === 'document_uploaded' && act.stage === 4) ||
                        (role === 'chef' && act.actionType === 'meal_prepared') ||
                        (role === 'delivery' && act.actionType === 'delivery_completed')
                      );
                      // Count unique staff IDs
                      const uniqueStaffCount = new Set(roleActivities.map(a => a.staffId)).size;

                      return (
                        <Card key={role}>
                          <CardHeader>
                            <CardTitle className="text-sm capitalize">{role.replace('_', ' ')}</CardTitle>
                          </CardHeader>
                          <CardContent>
                            <p className="text-2xl font-bold">{uniqueStaffCount}</p>
                            <p className="text-sm text-muted-foreground">Active staff</p>
                          </CardContent>
                        </Card>
                      );
                    })}
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Activity className="h-5 w-5" />
                    Recent Activities ({allActivities.slice(0, 10).length})
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-2 max-h-96 overflow-y-auto">
                    {allActivities.slice(0, 10).map(activity => (
                      <div key={activity.id} className="p-3 bg-muted rounded-md" data-testid={`activity-${activity.id}`}>
                        <p className="font-medium">{activity.description}</p>
                        <p className="text-sm text-muted-foreground">
                          Staff: {activity.staffId} | Customer: {activity.customerId}
                        </p>
                        <p className="text-xs text-muted-foreground">
                          {new Date(activity.createdAt).toLocaleString()}
                        </p>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="acknowledgements" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle>Acknowledgement Dashboard ({allAcknowledgements.length})</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-2 max-h-96 overflow-y-auto">
                    {allAcknowledgements.map(ack => (
                      <div key={ack.id} className="flex items-center justify-between p-3 bg-muted rounded-md" data-testid={`ack-${ack.id}`}>
                        <div>
                          <p className="font-medium">{ack.taskType}</p>
                          <p className="text-sm text-muted-foreground">
                            Staff: {ack.staffId} | Customer: {ack.customerId}
                          </p>
                          {ack.stage && <Badge variant="outline">Stage {ack.stage}</Badge>}
                        </div>
                        <Badge variant={
                          ack.status === 'completed' ? 'default' :
                          ack.status === 'acknowledged' ? 'secondary' :
                          'outline'
                        }>
                          {ack.status}
                        </Badge>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="delivery" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <MapPin className="h-5 w-5" />
                    Delivery Tracking ({deliveryLocations.length})
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-2 max-h-96 overflow-y-auto">
                    {deliveryLocations.map(location => (
                      <div key={location.id} className="p-3 bg-muted rounded-md" data-testid={`location-${location.id}`}>
                        <div className="flex items-center justify-between">
                          <div>
                            <p className="font-medium">Delivery Person: {location.deliveryPersonId}</p>
                            <p className="text-sm text-muted-foreground">
                              Lat: {location.latitude.toFixed(6)}, Lng: {location.longitude.toFixed(6)}
                            </p>
                            <p className="text-xs text-muted-foreground">
                              Updated: {new Date(location.updatedAt).toLocaleString()}
                            </p>
                          </div>
                          <Badge variant={location.status === 'on_delivery' ? 'default' : 'outline'}>
                            {location.status}
                          </Badge>
                        </div>
                      </div>
                    ))}
                    {deliveryLocations.length === 0 && (
                      <p className="text-center text-muted-foreground py-8">No delivery locations tracked</p>
                    )}
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            {selectedCustomer && (<>

              <TabsContent value="milestones" className="space-y-4">
                <Card>
                  <CardHeader>
                    <CardTitle>Customer Milestones</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    {milestones?.map((milestone) => (
                      <div
                        key={milestone.id}
                        className="flex items-center justify-between p-4 border rounded-md"
                        data-testid={`milestone-${milestone.id}`}
                      >
                        <div className="flex items-center gap-3">
                          {milestone.status === 'completed' ? (
                            <CheckCircle2 className="h-5 w-5 text-primary" />
                          ) : (
                            <Clock className="h-5 w-5 text-muted-foreground" />
                          )}
                          <div>
                            <p className="font-medium">{milestone.name}</p>
                            <p className="text-sm text-muted-foreground">
                              {milestone.status === 'completed' 
                                ? `Completed ${new Date(milestone.completedAt!).toLocaleDateString()}`
                                : 'In Progress'}
                            </p>
                          </div>
                        </div>
                        <Select
                          value={milestone.status}
                          onValueChange={(status) => 
                            updateMilestoneMutation.mutate({ id: milestone.id, status })
                          }
                          disabled={updateMilestoneMutation.isPending}
                        >
                          <SelectTrigger className="w-[150px]" data-testid={`select-milestone-status-${milestone.id}`}>
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="locked">Locked</SelectItem>
                            <SelectItem value="in_progress">In Progress</SelectItem>
                            <SelectItem value="completed">Completed</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                    ))}
                  </CardContent>
                </Card>
              </TabsContent>

              <TabsContent value="documents" className="space-y-4">
                <Card>
                  <CardHeader>
                    <CardTitle>Upload Document</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <form
                      onSubmit={(e) => {
                        e.preventDefault();
                        const formData = new FormData(e.currentTarget);
                        const url = formData.get('url') as string;
                        if (!url) {
                          toast({
                            title: "Error",
                            description: "Please upload a file first",
                            variant: "destructive",
                          });
                          return;
                        }
                        createDocumentMutation.mutate({
                          userId: selectedCustomer,
                          label: formData.get('label') as string,
                          url,
                          stage: parseInt(formData.get('stage') as string),
                        });
                        e.currentTarget.reset();
                      }}
                      className="space-y-4"
                    >
                      <div className="space-y-2">
                        <Label htmlFor="stage">Stage</Label>
                        <Select name="stage" required>
                          <SelectTrigger data-testid="select-document-stage">
                            <SelectValue placeholder="Select stage" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="1">1 - Physician Consultation</SelectItem>
                            <SelectItem value="2">2 - Test Collection</SelectItem>
                            <SelectItem value="3">3 - Discussion</SelectItem>
                            <SelectItem value="4">4 - Diet Chart</SelectItem>
                            <SelectItem value="5">5 - Meal Delivery</SelectItem>
                            <SelectItem value="6">6 - Final Report</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="label">Document Label</Label>
                        <Input
                          id="label"
                          name="label"
                          placeholder="Document label (e.g., Blood Test Results, Diet Plan)"
                          required
                          data-testid="input-document-label"
                        />
                      </div>

                      <div className="space-y-2">
                        <Label>File</Label>
                        <input type="hidden" name="url" id="documentUrl" />
                        <ObjectUploader
                          maxNumberOfFiles={1}
                          maxFileSize={10485760}
                          buttonVariant="outline"
                          onGetUploadParameters={async () => {
                            const response = await fetch('/api/objects/upload', {
                              method: 'POST',
                              credentials: 'include',
                            });
                            const data = await response.json();
                            return {
                              method: 'PUT' as const,
                              url: data.uploadURL,
                            };
                          }}
                          onComplete={(result: UploadResult<Record<string, unknown>, Record<string, unknown>>) => {
                            if (result.successful && result.successful.length > 0) {
                              const uploadURL = result.successful[0].uploadURL;
                              if (uploadURL) {
                                (document.getElementById('documentUrl') as HTMLInputElement).value = uploadURL;
                                toast({
                                  title: "File uploaded",
                                  description: "File uploaded successfully. Fill out the form and submit.",
                                });
                              }
                            }
                          }}
                        >
                          <Upload className="h-4 w-4 mr-2" />
                          Upload File
                        </ObjectUploader>
                      </div>

                      <Button
                        type="submit"
                        disabled={createDocumentMutation.isPending}
                        data-testid="button-upload-document"
                      >
                        {createDocumentMutation.isPending ? 'Uploading...' : 'Create Document'}
                      </Button>
                    </form>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader>
                    <CardTitle>Generate Reports</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <p className="text-sm text-muted-foreground mb-3">
                      Generate a consolidated clinical report combining all stages, documents, and health data
                    </p>
                    <Button
                      onClick={async () => {
                        if (!selectedCustomer) return;
                        try {
                          await apiRequest('POST', `/api/reports/${selectedCustomer.id}/consolidated`, {});
                          queryClient.invalidateQueries({ queryKey: ['/api/user/documents', selectedCustomer.id] });
                          toast({
                            title: "Report Generated",
                            description: "Consolidated clinical report has been generated successfully"
                          });
                        } catch (error) {
                          toast({
                            title: "Generation Failed",
                            description: "Failed to generate consolidated report",
                            variant: "destructive"
                          });
                        }
                      }}
                      variant="secondary"
                      className="w-full"
                      data-testid="button-generate-consolidated-report"
                    >
                      <FileText className="h-4 w-4 mr-2" />
                      Generate Consolidated Clinical Report
                    </Button>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader>
                    <CardTitle>Uploaded Documents</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    {documents?.map((document) => (
                      <div
                        key={document.id}
                        className="p-4 border rounded-md"
                        data-testid={`document-${document.id}`}
                      >
                        <div className="flex items-start justify-between mb-2">
                          <div className="flex items-center gap-2">
                            <FileText className="h-5 w-5 text-primary" />
                            <div>
                              <p className="font-medium">{document.label || 'Untitled Document'}</p>
                              <Badge variant="outline" className="mt-1">
                                Stage {document.stage}
                              </Badge>
                            </div>
                          </div>
                          {document.uploadedByRole && (
                            <Badge variant="secondary">{document.uploadedByRole}</Badge>
                          )}
                        </div>
                        <div className="flex items-center gap-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => window.open(document.url, '_blank')}
                            data-testid={`button-view-document-${document.id}`}
                          >
                            View File
                          </Button>
                          <p className="text-xs text-muted-foreground">
                            Uploaded {new Date(document.createdAt!).toLocaleDateString()}
                          </p>
                        </div>
                      </div>
                    ))}
                  </CardContent>
                </Card>
              </TabsContent>

              <TabsContent value="reports" className="space-y-4">
                <Card>
                  <CardHeader>
                    <CardTitle>Create New Report</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <form
                      onSubmit={(e) => {
                        e.preventDefault();
                        const formData = new FormData(e.currentTarget);
                        createReportMutation.mutate({
                          userId: selectedCustomer,
                          reportType: formData.get('reportType') as string,
                          title: formData.get('title') as string,
                          summary: formData.get('summary') as string,
                        });
                        e.currentTarget.reset();
                      }}
                      className="space-y-4"
                    >
                      <div className="space-y-2">
                        <Label htmlFor="reportType">Report Type</Label>
                        <Select name="reportType" required>
                          <SelectTrigger data-testid="select-report-type">
                            <SelectValue placeholder="Select type" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="test_result">Test Result</SelectItem>
                            <SelectItem value="diagnosis">Diagnosis</SelectItem>
                            <SelectItem value="diet_chart">Diet Chart</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="title">Title</Label>
                        <Input
                          id="title"
                          name="title"
                          placeholder="Report title"
                          required
                          data-testid="input-report-title"
                        />
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="summary">Summary</Label>
                        <Textarea
                          id="summary"
                          name="summary"
                          placeholder="Report summary or findings"
                          rows={4}
                          required
                          data-testid="textarea-report-summary"
                        />
                      </div>

                      <Button
                        type="submit"
                        disabled={createReportMutation.isPending}
                        data-testid="button-create-report"
                      >
                        {createReportMutation.isPending ? 'Creating...' : 'Create Report'}
                      </Button>
                    </form>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader>
                    <CardTitle>Existing Reports</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    {reports?.map((report) => (
                      <div
                        key={report.id}
                        className="p-4 border rounded-md"
                        data-testid={`report-${report.id}`}
                      >
                        <div className="flex items-start justify-between mb-2">
                          <div className="flex items-center gap-2">
                            <FileText className="h-5 w-5 text-primary" />
                            <p className="font-medium">{report.title}</p>
                          </div>
                          <Badge variant="secondary">{report.reportType.replace('_', ' ')}</Badge>
                        </div>
                        <p className="text-sm text-muted-foreground">{report.summary}</p>
                        <p className="text-xs text-muted-foreground mt-2">
                          Created {new Date(report.createdAt!).toLocaleDateString()}
                        </p>
                      </div>
                    ))}
                  </CardContent>
                </Card>
              </TabsContent>
            </>)}
          </Tabs>
        </div>
      </div>
    </div>
  );
}

client/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 100%;

  --foreground: 0 0% 13%;

  --border: 0 0% 89%;

  --card: 0 0% 98%;

  --card-foreground: 0 0% 13%;

  --card-border: 0 0% 94%;

  --sidebar: 0 0% 96%;

  --sidebar-foreground: 0 0% 13%;

  --sidebar-border: 0 0% 91%;

  --sidebar-primary: 142 71% 45%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 142 15% 92%;

  --sidebar-accent-foreground: 142 71% 25%;

  --sidebar-ring: 142 71% 45%;

  --popover: 0 0% 96%;

  --popover-foreground: 0 0% 13%;

  --popover-border: 0 0% 91%;

  --primary: 142 71% 45%;

  --primary-foreground: 0 0% 100%;

  --secondary: 0 0% 92%;

  --secondary-foreground: 0 0% 13%;

  --muted: 142 8% 94%;

  --muted-foreground: 0 0% 45%;

  --accent: 142 12% 93%;

  --accent-foreground: 0 0% 13%;

  --destructive: 0 84% 48%;

  --destructive-foreground: 0 0% 100%;

  --input: 0 0% 75%;
  --ring: 142 71% 45%;
  --chart-1: 142 71% 45%;
  --chart-2: 24 100% 50%;
  --chart-3: 217 91% 60%;
  --chart-4: 45 93% 47%;
  --chart-5: 280 65% 55%;

  --font-sans: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'JetBrains Mono', Menlo, monospace;
  --font-display: 'Plus Jakarta Sans', 'Inter', sans-serif;
  --radius: .5rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(0 0% 0% / 0.10);
  --shadow-sm: 0px 2px 4px -1px hsl(0 0% 0% / 0.06), 0px 1px 2px -1px hsl(0 0% 0% / 0.06);
  --shadow: 0px 4px 6px -1px hsl(0 0% 0% / 0.07), 0px 2px 4px -2px hsl(0 0% 0% / 0.05);
  --shadow-md: 0px 6px 16px -4px hsl(0 0% 0% / 0.08), 0px 4px 6px -2px hsl(0 0% 0% / 0.03);
  --shadow-lg: 0px 10px 24px -4px hsl(0 0% 0% / 0.10), 0px 8px 8px -4px hsl(0 0% 0% / 0.04);
  --shadow-xl: 0px 20px 32px -8px hsl(0 0% 0% / 0.12), 0px 8px 16px -8px hsl(0 0% 0% / 0.08);
  --shadow-2xl: 0px 32px 64px -12px hsl(0 0% 0% / 0.14);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 0 0% 9%;

  --foreground: 0 0% 95%;

  --border: 0 0% 23%;

  --card: 0 0% 13%;

  --card-foreground: 0 0% 95%;

  --card-border: 0 0% 18%;

  --sidebar: 0 0% 15%;

  --sidebar-foreground: 0 0% 95%;

  --sidebar-border: 0 0% 20%;

  --sidebar-primary: 142 65% 55%;

  --sidebar-primary-foreground: 0 0% 9%;

  --sidebar-accent: 142 18% 22%;

  --sidebar-accent-foreground: 142 65% 75%;

  --sidebar-ring: 142 65% 55%;

  --popover: 0 0% 15%;

  --popover-foreground: 0 0% 95%;

  --popover-border: 0 0% 20%;

  --primary: 142 65% 55%;

  --primary-foreground: 0 0% 9%;

  --secondary: 0 0% 21%;

  --secondary-foreground: 0 0% 95%;

  --muted: 142 8% 18%;

  --muted-foreground: 0 0% 65%;

  --accent: 142 10% 19%;

  --accent-foreground: 0 0% 95%;

  --destructive: 0 75% 50%;

  --destructive-foreground: 0 0% 100%;

  --input: 0 0% 35%;
  --ring: 142 65% 55%;
  --chart-1: 142 65% 65%;
  --chart-2: 24 95% 65%;
  --chart-3: 217 91% 70%;
  --chart-4: 45 93% 60%;
  --chart-5: 280 65% 70%;

  --shadow-2xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.30);
  --shadow-xs: 0px 1px 3px 0px hsl(0 0% 0% / 0.40);
  --shadow-sm: 0px 2px 4px -1px hsl(0 0% 0% / 0.35), 0px 1px 2px -1px hsl(0 0% 0% / 0.30);
  --shadow: 0px 4px 6px -1px hsl(0 0% 0% / 0.40), 0px 2px 4px -2px hsl(0 0% 0% / 0.35);
  --shadow-md: 0px 6px 16px -4px hsl(0 0% 0% / 0.45), 0px 4px 6px -2px hsl(0 0% 0% / 0.30);
  --shadow-lg: 0px 10px 24px -4px hsl(0 0% 0% / 0.50), 0px 8px 8px -4px hsl(0 0% 0% / 0.35);
  --shadow-xl: 0px 20px 32px -8px hsl(0 0% 0% / 0.55), 0px 8px 16px -8px hsl(0 0% 0% / 0.40);
  --shadow-2xl: 0px 32px 64px -12px hsl(0 0% 0% / 0.60);

  /* Automatically computed borders */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }
}

