                )}
              </div>

              {isPlanLoading ? (
                <div className="grid md:grid-cols-3 gap-4">
                  {[1, 2, 3].map((i) => (
                    <Card key={i}>
                      <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
                        <div className="h-4 w-20 bg-muted animate-pulse rounded" />
                        <div className="h-4 w-4 bg-muted animate-pulse rounded-full" />
                      </CardHeader>
                      <CardContent>
                        <div className="h-8 w-32 bg-muted animate-pulse rounded mb-2" />
                        <div className="h-3 w-24 bg-muted animate-pulse rounded" />
                      </CardContent>
                    </Card>
                  ))}
                </div>
              ) : plan ? (
                <div className="grid md:grid-cols-3 gap-4">
                  <Card>
                    <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
                      <CardTitle className="text-sm font-medium">Plan Type</CardTitle>
                      <Package className="h-4 w-4 text-primary" />
                    </CardHeader>
                    <CardContent>
                      <div className="text-2xl font-semibold" data-testid="text-plan-type">
                        {plan.type}
                      </div>
                      <p className="text-xs text-muted-foreground mt-1">
                        {plan.durationDays} days duration
                      </p>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
                      <CardTitle className="text-sm font-medium">Plan Value</CardTitle>
                      <CreditCard className="h-4 w-4 text-chart-2" />
                    </CardHeader>
                    <CardContent>
                      <div className="text-2xl font-mono font-semibold" data-testid="text-plan-value">
                        {parseFloat(plan.listPrice).toLocaleString('en-IN')}
                      </div>
                      <p className="text-xs text-muted-foreground mt-1">
                        Total plan value
                      </p>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
                      <CardTitle className="text-sm font-medium">Status</CardTitle>
                      <Activity className="h-4 w-4 text-chart-3" />
                    </CardHeader>
                    <CardContent>
                      <div className="flex items-center gap-2">
                        <Badge variant={plan.isActive ? 'default' : 'secondary'} data-testid="badge-plan-status">
                          {plan.isActive ? 'Active' : 'Pending Activation'}
                        </Badge>
                      </div>
                      {plan.startDate && (
                        <p className="text-xs text-muted-foreground mt-2">
                          Started: {plan.startDate}
                        </p>
                      )}
                    </CardContent>
                  </Card>
                </div>
              ) : null}

              <Card className="border-primary/30 bg-primary/5">
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Activity className="h-5 w-5" />
                    Your Clinical Journey - 6 Stage Process
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  {isMilestonesLoading ? (
                    <div className="flex justify-center py-12">
                      <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
                    </div>
                  ) : milestones.length > 0 ? (
                    <>
                      <div className="relative">
                        {/* Horizontal connector line */}
                        <div className="absolute top-6 left-0 right-0 h-0.5 bg-border" aria-hidden="true" />

                        {/* Stage cards in horizontal layout */}
                        <div className="relative flex justify-between gap-2 overflow-x-auto pb-4">
                          {milestones.map((milestone, index) => {
                            const StatusIcon = getStatusIcon(milestone.status);
                            const statusColor = getStatusColor(milestone.status);
                            const isCompleted = milestone.status === 'completed';
                            const isInProgress = milestone.status === 'in_progress';

                            return (
                              <div
                                key={milestone.id}
                                className="flex flex-col items-center min-w-[140px] flex-1"
                                data-testid={`milestone-${milestone.id}`}
                              >
                                {/* Stage number and icon */}
                                <div className={`relative z-10 flex h-12 w-12 items-center justify-center rounded-full border-2 ${
                                  isCompleted ? 'bg-primary border-primary' :
                                  isInProgress ? 'bg-chart-2 border-chart-2' :
                                  'bg-background border-border'
                                }`}>
                                  <StatusIcon className={`h-6 w-6 ${
                                    isCompleted || isInProgress ? 'text-white' : 'text-muted-foreground'
                                  }`} />
                                </div>

                                {/* Stage name and status */}
                                <div className="mt-3 text-center">
                                  <p className={`text-xs font-medium ${
                                    isCompleted || isInProgress ? 'text-foreground' : 'text-muted-foreground'
                                  }`} data-testid={`text-milestone-${milestone.id}`}>
                                    Stage {index + 1}
                                  </p>
                                  <p className={`text-xs mt-1 ${
                                    isCompleted || isInProgress ? 'text-foreground' : 'text-muted-foreground'
                                  }`}>
                                    {milestone.name}
                                  </p>
                                  {isInProgress && (
                                    <Badge variant="outline" className="text-xs mt-2">
                                      In Progress
                                    </Badge>
                                  )}
                                </div>
                              </div>
                            );
                          })}
                        </div>
                      </div>

                      <div className="mt-6 pt-6 border-t">
                        <div className="flex items-center justify-between mb-3">
                          <p className="text-sm font-medium">Overall Progress</p>
                          <p className="text-sm text-muted-foreground">
                            {completedCount} of {totalMilestones} stages completed
                          </p>
                        </div>
                        <Progress value={progressPercentage} data-testid="progress-milestones" className="h-2" />
                      </div>
                    </>
                  ) : (
                    <p className="text-center py-8 text-muted-foreground">
                      No milestones available yet. Complete your consultation to begin your journey.
                    </p>
                  )}
                </CardContent>
              </Card>

              <div className="grid md:grid-cols-3 gap-6">
                {stats.map((stat, index) => (
                  <Card key={index} data-testid={`card-stat-${index}`}>
                    <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
                      <CardTitle className="text-sm font-medium" data-testid={`text-stat-label-${index}`}>
                        {stat.label}
                      </CardTitle>
                      <stat.icon className={`h-4 w-4 ${stat.color}`} />
                    </CardHeader>
                    <CardContent>
                      <div className="text-2xl font-mono font-semibold" data-testid={`text-stat-value-${index}`}>
                        {stat.value}
                      </div>
                      <p className="text-xs text-muted-foreground mt-1">
                        Target: {stat.target}
                      </p>
                      <Progress 
                        value={(parseInt(stat.value) / parseInt(stat.target)) * 100} 
                        className="mt-3"
                        data-testid={`progress-stat-${index}`}
                      />
                    </CardContent>
                  </Card>
                ))}
              </div>

              <Tabs defaultValue="week" className="space-y-6">
                <TabsList data-testid="tabs-meal-schedule">
                  <TabsTrigger value="week" data-testid="tab-week">This Week</TabsTrigger>
                  <TabsTrigger value="reports" data-testid="tab-reports">Reports</TabsTrigger>
                  <TabsTrigger value="profile" data-testid="tab-profile">Profile</TabsTrigger>
                  <TabsTrigger value="settings" data-testid="tab-settings">Settings</TabsTrigger>
                </TabsList>

                <TabsContent value="week" className="space-y-6">
                  <Card>
                    <CardHeader>
                      <CardTitle data-testid="text-week-meals">This Week's Meal Plan</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-8">
                      {weeklyMeals.map((dayPlan, dayIndex) => (
                        <div key={dayPlan.day} className="space-y-4">
                          <div className="flex items-center gap-2">
                            <h3 className="font-semibold text-lg" data-testid={`text-day-${dayIndex}`}>
                              {dayPlan.day}
                            </h3>
                            <Badge variant="outline" className="text-xs">
                              {dayPlan.meals.reduce((sum, meal) => sum + meal.calories, 0)} kcal
                            </Badge>
                          </div>
                          <div className="grid md:grid-cols-3 gap-6">
                            {dayPlan.meals.map((meal, mealIndex) => (
                              <MealCard
                                key={`${dayIndex}-${mealIndex}`}
                                {...meal}
                                onSwap={() => console.log(`Swap ${meal.name} on ${dayPlan.day}`)}
                              />
                            ))}
                          </div>
                        </div>
                      ))}
                      <div className="text-center pt-4">
                        <p className="text-sm text-muted-foreground mb-4">
                          Showing meals for the first 3 days. Full week available after plan activation.
                        </p>
                      </div>
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="reports" className="space-y-6">
                  <Card>
                    <CardHeader>
                      <CardTitle data-testid="text-reports">Your Health Reports</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-4">
                      <div className="space-y-3">
                        <Button
                          variant="outline"
                          className="w-full justify-between"
                          data-testid="button-view-test-report"
                        >
                          <span>Blood Test Report</span>
                          <Badge>Available</Badge>
                        </Button>
                        <Button
                          variant="outline"
                          className="w-full justify-between"
                          disabled
                          data-testid="button-view-diagnosis"
                        >
                          <span>Diagnosis Summary</span>
                          <Badge variant="secondary">Pending</Badge>
                        </Button>
                        <Button
                          variant="outline"
                          className="w-full justify-between"
                          disabled
                          data-testid="button-view-diet-chart"
                        >
                          <span>Personalized Diet Chart</span>
                          <Badge variant="secondary">Locked</Badge>
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="profile" className="space-y-6">
                  <Card>
                    <CardHeader>
                      <CardTitle data-testid="text-profile">Consultation Details</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-4">
                      {latestConsultation ? (
                        <div className="grid md:grid-cols-2 gap-4">
                          <div className="space-y-2">
                            <p className="text-sm text-muted-foreground">Doctor</p>
                            <p className="font-medium" data-testid="text-doctor">
                              {latestConsultation.doctorName || 'Not assigned'}
                            </p>
                          </div>
                          <div className="space-y-2">
                            <p className="text-sm text-muted-foreground">Date</p>
                            <p className="font-medium" data-testid="text-date">
                              {latestConsultation.scheduledDate}
                            </p>
                          </div>
                          <div className="space-y-2">
                            <p className="text-sm text-muted-foreground">Time</p>
                            <p className="font-medium" data-testid="text-time">
                              {latestConsultation.scheduledTime}
                            </p>
                          </div>
                          <div className="space-y-2">
                            <p className="text-sm text-muted-foreground">Status</p>
                            <p className="font-medium" data-testid="text-status">
                              <Badge variant={latestConsultation.status === 'scheduled' ? 'default' : 'secondary'}>
                                {latestConsultation.status}
                              </Badge>
                            </p>
                          </div>
                          <div className="space-y-2">
                            <p className="text-sm text-muted-foreground">Meeting Type</p>
                            <p className="font-medium capitalize" data-testid="text-meeting-type">
                              {latestConsultation.meetingType}
                            </p>
                          </div>
                        </div>
                      ) : (
                        <p className="text-center py-8 text-muted-foreground">
                          No consultation booked yet.
                        </p>
                      )}
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="settings" className="space-y-6">
                  <Card>
                    <CardHeader>
                      <CardTitle data-testid="text-preferences">Preferences</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-4">
                      <Button variant="outline" className="w-full justify-start" data-testid="button-update-preferences">
                        Update Dietary Preferences
                      </Button>
                      <Button variant="outline" className="w-full justify-start" data-testid="button-delivery-schedule">
                        Manage Delivery Schedule
                      </Button>
                      <Button variant="outline" className="w-full justify-start" data-testid="button-subscription">
                        Subscription Settings
                      </Button>
                    </CardContent>
                  </Card>
                </TabsContent>
              </Tabs>
            </div>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
}

