import { useEffect } from 'react';
import { useLocation } from 'wouter';
import { Header } from '@/components/Header';
import { Footer } from '@/components/Footer';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { CheckCircle2, Calendar, User, ArrowRight } from 'lucide-react';

export default function PaymentSuccess() {
  const [, navigate] = useLocation();

  const consultationData = JSON.parse(localStorage.getItem('consultationData') || '{}');
  const signupData = JSON.parse(localStorage.getItem('signupData') || '{}');

  useEffect(() => {
    // Auto-redirect to login after 3 seconds
    const timer = setTimeout(() => {
      window.location.href = '/api/login';
    }, 3000);

    return () => {
      clearTimeout(timer);
      localStorage.removeItem('consultationData');
      localStorage.removeItem('signupData');
      localStorage.removeItem('pendingUserId');
      localStorage.removeItem('pendingPaymentSessionId');
      localStorage.removeItem('userEmail');
    };
  }, []);

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-b from-emerald-50/30 via-white to-blue-50/20">
      <Header />
      <main className="flex-1 flex items-center justify-center px-4 py-12">
        <div className="max-w-2xl w-full space-y-8">
          <Card className="text-center">
            <CardContent className="p-12 space-y-6">
              <div className="flex justify-center">
                <div className="h-24 w-24 rounded-full bg-emerald-100 flex items-center justify-center">
                  <CheckCircle2 className="h-14 w-14 text-emerald-600" data-testid="icon-success" />
                </div>
              </div>

              <div className="space-y-2">
                <h1 className="text-3xl font-bold" data-testid="text-success-title">
                  Payment Successful!
                </h1>
                <p className="text-lg text-muted-foreground">
                  Your consultation has been booked successfully
                </p>
              </div>

              <div className="bg-muted/30 rounded-lg p-6 space-y-4 text-left">
                <h3 className="font-semibold text-center mb-4">Booking Details</h3>

                <div className="space-y-3">
                  <div className="flex items-center gap-3">
                    <User className="h-5 w-5 text-primary" />
                    <div className="flex-1">
                      <p className="text-sm text-muted-foreground">Patient Name</p>
                      <p className="font-medium" data-testid="text-patient-name">{signupData.name}</p>
                    </div>
                  </div>

                  {consultationData.doctorName && (
                    <div className="flex items-center gap-3">
                      <User className="h-5 w-5 text-primary" />
                      <div className="flex-1">
                        <p className="text-sm text-muted-foreground">Doctor</p>
                        <p className="font-medium" data-testid="text-doctor">{consultationData.doctorName}</p>
                      </div>
                    </div>
                  )}

                  {consultationData.date && consultationData.time && (
                    <div className="flex items-center gap-3">
                      <Calendar className="h-5 w-5 text-primary" />
                      <div className="flex-1">
                        <p className="text-sm text-muted-foreground">Appointment</p>
                        <p className="font-medium" data-testid="text-appointment">
                          {consultationData.date} at {consultationData.time}
                        </p>
                      </div>
                    </div>
                  )}
                </div>
              </div>

              <div className="space-y-3 pt-4">
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 text-sm text-blue-900">
                  <p className="font-semibold mb-1">What's Next?</p>
                  <p>Redirecting you to sign in... You'll be able to access your dashboard and track your progress through our 5-stage wellness journey.</p>
                </div>

                <div className="flex items-center justify-center gap-2 text-sm text-muted-foreground">
                  <div className="animate-spin h-4 w-4 border-2 border-primary border-t-transparent rounded-full" />
                  <span>Preparing your dashboard...</span>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </main>
      <Footer />
    </div>
  );
}

client/src/pages/SignupCharacter.tsx.content:
import { useState } from 'react';
import { useLocation } from 'wouter';
import { Header } from '@/components/Header';
import { Footer } from '@/components/Footer';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { ArrowRight, Upload, User, Mail, Phone, Lock } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

const presetCharacters = [
  { id: 'chef', name: 'Chef Alex', emoji: '', color: 'bg-orange-100' },
  { id: 'athlete', name: 'Athletic Ana', emoji: '', color: 'bg-blue-100' },
  { id: 'professional', name: 'Pro Pat', emoji: '', color: 'bg-purple-100' },
  { id: 'student', name: 'Student Sam', emoji: '', color: 'bg-green-100' },
  { id: 'parent', name: 'Parent Parker', emoji: '', color: 'bg-pink-100' },
  { id: 'senior', name: 'Senior Sarah', emoji: '', color: 'bg-amber-100' },
];

export default function SignupCharacter() {
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const [step, setStep] = useState<'signup' | 'character'>('signup');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
  });
  const [selectedCharacter, setSelectedCharacter] = useState('');
  const [uploadedImage, setUploadedImage] = useState('');

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSignupSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.email || !formData.phone || !formData.password) {
      toast({
        title: 'Missing Information',
        description: 'Please fill in all fields',
        variant: 'destructive',
      });
      return;
    }

    setStep('character');
  };

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setUploadedImage(reader.result as string);
        setSelectedCharacter('uploaded');
      };
      reader.readAsDataURL(file);
    }
  };

  const handleContinue = async () => {
    if (!selectedCharacter) {
      toast({
        title: 'Select Your Character',
        description: 'Please choose a character to represent you',
        variant: 'destructive',
      });
      return;
    }

    const consultationData = JSON.parse(localStorage.getItem('consultationData') || '{}');

    try {
      const response = await fetch('/api/signup-with-consultation', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          phone: formData.phone,
          characterType: selectedCharacter,
          characterImage: selectedCharacter === 'uploaded' ? uploadedImage : undefined,
          consultationDate: consultationData.date,
          consultationTime: consultationData.time,
          doctorName: consultationData.doctorName,
          planType: consultationData.planType,
        }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Signup failed');
      }

      const data = await response.json();

      // Store userId and payment session ID for later
      localStorage.setItem('pendingUserId', data.userId);
      localStorage.setItem('pendingPaymentSessionId', data.paymentSessionId);
      localStorage.setItem('userEmail', formData.email);

      navigate('/payment');
    } catch (error) {
      toast({
        title: 'Signup Failed',
        description: error instanceof Error ? error.message : 'Something went wrong',
        variant: 'destructive',
      });
    }
  };

  if (step === 'signup') {
    return (
      <div className="min-h-screen flex flex-col bg-gradient-to-b from-blue-50/30 via-white to-emerald-50/20">
        <Header />
        <main className="flex-1 flex items-center justify-center py-12 px-4">
          <Card className="w-full max-w-md">
            <CardHeader>
              <CardTitle className="text-2xl text-center">Create Your Account</CardTitle>
              <p className="text-center text-muted-foreground">
                Join us on your journey to better health
              </p>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSignupSubmit} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="name">Full Name</Label>
                  <div className="relative">
                    <User className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                    <Input
                      id="name"
                      name="name"
                      placeholder="John Doe"
                      value={formData.name}
                      onChange={handleInputChange}
                      className="pl-10"
                      data-testid="input-name"
                    />
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="email">Email</Label>
                  <div className="relative">
                    <Mail className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                    <Input
                      id="email"
                      name="email"
                      type="email"
                      placeholder="john@example.com"
                      value={formData.email}
                      onChange={handleInputChange}
                      className="pl-10"
                      data-testid="input-email"
                    />
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="phone">Phone Number</Label>
                  <div className="relative">
                    <Phone className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                    <Input
                      id="phone"
                      name="phone"
                      type="tel"
                      placeholder="+91 98765 43210"
                      value={formData.phone}
                      onChange={handleInputChange}
                      className="pl-10"
                      data-testid="input-phone"
                    />
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="password">Password</Label>
                  <div className="relative">
                    <Lock className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                    <Input
                      id="password"
                      name="password"
                      type="password"
                      placeholder=""
                      value={formData.password}
                      onChange={handleInputChange}
                      className="pl-10"
                      data-testid="input-password"
                    />
                  </div>
                </div>

                <Button type="submit" size="lg" className="w-full" data-testid="button-next-character">
                  Next: Create Character
                  <ArrowRight className="ml-2 h-4 w-4" />
                </Button>
              </form>
            </CardContent>
          </Card>
        </main>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-b from-blue-50/30 via-white to-emerald-50/20">
      <Header />
      <main className="flex-1 py-12 px-4">
        <div className="container mx-auto max-w-5xl space-y-8">
          <div className="text-center space-y-3">
            <h1 className="font-display text-3xl md:text-4xl font-bold" data-testid="text-character-title">
              Create Your Character
            </h1>
            <p className="text-lg text-muted-foreground">
              Choose an avatar that represents you on your wellness journey
            </p>
          </div>

          <div className="grid md:grid-cols-2 gap-8">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Upload className="h-5 w-5" />
                  Upload Your Photo
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div
                    className={`border-2 border-dashed rounded-lg p-8 text-center transition-all ${
                      uploadedImage ? 'border-primary bg-primary/5' : 'border-border'
                    }`}
                  >
                    {uploadedImage ? (
                      <div className="space-y-3">
                        <img
                          src={uploadedImage}
                          alt="Uploaded"
                          className="w-32 h-32 rounded-full object-cover mx-auto border-4 border-primary"
                        />
                        <p className="text-sm text-muted-foreground">Photo uploaded successfully!</p>
                      </div>
                    ) : (
                      <div className="space-y-3">
                        <div className="w-20 h-20 rounded-full bg-muted mx-auto flex items-center justify-center">
                          <Upload className="h-8 w-8 text-muted-foreground" />
                        </div>
                        <p className="text-sm text-muted-foreground">
                          Upload a selfie to create your AI-generated avatar
                        </p>
                      </div>
                    )}
                  </div>
                  <Input
                    type="file"
                    accept="image/*"
                    onChange={handleImageUpload}
                    className="cursor-pointer"
                    data-testid="input-upload-photo"
                  />
                  <p className="text-xs text-muted-foreground">
                    AI avatar generation coming soon! For now, we'll use your photo.
                  </p>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>Choose a Preset Character</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 gap-3">
                  {presetCharacters.map((char) => (
                    <button
                      key={char.id}
                      onClick={() => {
                        setSelectedCharacter(char.id);
                        setUploadedImage('');
                      }}
                      className={`p-4 rounded-lg border-2 transition-all text-center hover-elevate ${
                        selectedCharacter === char.id
                          ? 'border-primary bg-primary/5'
                          : 'border-border'
                      }`}
                      data-testid={`button-character-${char.id}`}
                    >
                      <div className={`text-4xl mb-2 p-3 rounded-full ${char.color} inline-block`}>
                        {char.emoji}
                      </div>
                      <div className="text-sm font-medium">{char.name}</div>
                    </button>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>

          {selectedCharacter && (
            <div className="flex justify-center">
              <Card className="max-w-md w-full">
                <CardContent className="p-6 flex items-center justify-between">
                  <div className="flex items-center gap-4">
                    {selectedCharacter === 'uploaded' && uploadedImage ? (
                      <img
                        src={uploadedImage}
                        alt="Selected"
                        className="w-16 h-16 rounded-full object-cover border-2 border-primary"
                      />
                    ) : (
                      <div className={`text-3xl p-3 rounded-full ${
                        presetCharacters.find(c => c.id === selectedCharacter)?.color
                      }`}>
                        {presetCharacters.find(c => c.id === selectedCharacter)?.emoji}
                      </div>
                    )}
                    <div>
                      <p className="font-semibold">Character Selected</p>
                      <p className="text-sm text-muted-foreground" data-testid="text-selected-character">
                        {selectedCharacter === 'uploaded'
                          ? 'Your Photo'
                          : presetCharacters.find(c => c.id === selectedCharacter)?.name}
                      </p>
                    </div>
                  </div>
                  <Button onClick={handleContinue} data-testid="button-proceed-payment">
                    Proceed to Payment
                    <ArrowRight className="ml-2 h-4 w-4" />
                  </Button>
                </CardContent>
              </Card>
            </div>
          )}
        </div>
      </main>
      <Footer />
    </div>
  );
}

client/src/pages/DummyPayment.tsx.content:
import { useState } from 'react';
import { useLocation } from 'wouter';
import { useQuery } from '@tanstack/react-query';
import { Header } from '@/components/Header';
import { Footer } from '@/components/Footer';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { CreditCard, Lock, CheckCircle2, Loader2 } from 'lucide-react';

type Plan = {
  id: string;
  listPrice: string;
  consultationFeeCredited: string;
  finalPayable: string;
  type: string;
};

export default function DummyPayment() {
  const [, navigate] = useLocation();
  const [isProcessing, setIsProcessing] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const [cardData, setCardData] = useState({
    name: '',
    number: '',
    expiry: '',
    cvv: '',
  });

  // Fetch user's active plan
  const { data: plan, isLoading: isPlanLoading } = useQuery<Plan>({
    queryKey: ['/api/user/plan'],
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setCardData({
      ...cardData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePayment = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsProcessing(true);

    try {
      // Simulate payment processing
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Get stored data from localStorage
      const userId = localStorage.getItem('pendingUserId');
      const paymentSessionId = localStorage.getItem('pendingPaymentSessionId');

      if (!userId || !paymentSessionId) {
        throw new Error('Missing payment session data');
      }

      // Call backend to complete payment
      const response = await fetch('/api/payment/complete-dummy', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId,
          paymentSessionId,
        }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Payment failed');
      }

      setIsProcessing(false);
      setIsSuccess(true);

      setTimeout(() => {
        navigate('/payment-success');
      }, 2000);
    } catch (error) {
      setIsProcessing(false);
      console.error('Payment error:', error);
      alert(error instanceof Error ? error.message : 'Payment failed. Please try again.');
    }
  };

  const consultationData = JSON.parse(localStorage.getItem('consultationData') || '{}');
  const signupData = JSON.parse(localStorage.getItem('signupData') || '{}');

  if (isSuccess) {
    return (
      <div className="min-h-screen flex flex-col bg-gradient-to-b from-emerald-50/30 via-white to-blue-50/20">
        <Header />
        <main className="flex-1 flex items-center justify-center px-4">
          <Card className="max-w-md w-full text-center">
            <CardContent className="p-12 space-y-6">
              <div className="flex justify-center">
                <div className="h-20 w-20 rounded-full bg-emerald-100 flex items-center justify-center">
                  <CheckCircle2 className="h-12 w-12 text-emerald-600" data-testid="icon-success" />
                </div>
              </div>
              <div>
                <h2 className="text-2xl font-bold mb-2" data-testid="text-success-title">
                  Payment Successful!
                </h2>
                <p className="text-muted-foreground">
                  Your consultation has been booked. Redirecting to your dashboard...
                </p>
              </div>
            </CardContent>
          </Card>
        </main>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-b from-blue-50/30 via-white to-emerald-50/20">
      <Header />
      <main className="flex-1 py-12 px-4">
        <div className="container mx-auto max-w-4xl">
          <div className="grid md:grid-cols-3 gap-8">
            <div className="md:col-span-2">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <CreditCard className="h-5 w-5" />
                    Payment Details
                  </CardTitle>
                  <p className="text-sm text-muted-foreground">
                    This is a test payment gateway. No real charges will be made.
                  </p>
                </CardHeader>
                <CardContent>
                  <form onSubmit={handlePayment} className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="name">Name on Card</Label>
                      <Input
                        id="name"
                        name="name"
                        placeholder="JOHN DOE"
                        value={cardData.name}
                        onChange={handleInputChange}
                        required
                        data-testid="input-card-name"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="number">Card Number</Label>
                      <Input
                        id="number"
                        name="number"
                        placeholder="1234 5678 9012 3456"
                        maxLength={19}
                        value={cardData.number}
                        onChange={handleInputChange}
                        required
                        data-testid="input-card-number"
                      />
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <Label htmlFor="expiry">Expiry Date</Label>
                        <Input
                          id="expiry"
                          name="expiry"
                          placeholder="MM/YY"
                          maxLength={5}
                          value={cardData.expiry}
                          onChange={handleInputChange}
                          required
                          data-testid="input-card-expiry"
                        />
                      </div>
                      <div className="space-y-2">
                        <Label htmlFor="cvv">CVV</Label>
                        <Input
                          id="cvv"
                          name="cvv"
                          type="password"
                          placeholder="123"
                          maxLength={3}
                          value={cardData.cvv}
                          onChange={handleInputChange}
                          required
                          data-testid="input-card-cvv"
                        />
                      </div>
                    </div>

                    <div className="flex items-center gap-2 p-4 bg-muted/50 rounded-lg">
                      <Lock className="h-4 w-4 text-muted-foreground" />
                      <p className="text-xs text-muted-foreground">
                        Your payment is secure and encrypted. This is a demo gateway for testing.
                      </p>
                    </div>

                    <Button
                      type="submit"
                      size="lg"
                      className="w-full"
                      disabled={isProcessing || isPlanLoading || !plan}
                      data-testid="button-pay-now"
                    >
                      {isProcessing ? (
                        <span className="flex items-center gap-2">
                          <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full" />
                          Processing...
                        </span>
                      ) : plan ? (
                        `Pay ${parseFloat(plan.finalPayable).toLocaleString('en-IN')}`
                      ) : (
                        'Loading...'
                      )}
                    </Button>
                  </form>
                </CardContent>
              </Card>
            </div>

            <div className="md:col-span-1">
              <Card className="sticky top-4">
                <CardHeader>
                  <CardTitle>Order Summary</CardTitle>
                </CardHeader>
                <CardContent className="space-y-6">
                  <div className="space-y-3">
                    <div className="flex justify-between text-sm">
                      <span className="text-muted-foreground">Plan</span>
                      <span className="font-medium" data-testid="text-plan-type">
                        {consultationData.planType || 'Clinical-Level Guided'}
                      </span>
                    </div>

                    {consultationData.doctorName && (
                      <div className="flex justify-between text-sm">
                        <span className="text-muted-foreground">Doctor</span>
                        <span className="font-medium" data-testid="text-doctor">
                          {consultationData.doctorName}
                        </span>
                      </div>
                    )}

                    {consultationData.date && (
                      <div className="flex justify-between text-sm">
                        <span className="text-muted-foreground">Date</span>
                        <span className="font-medium" data-testid="text-date">
                          {consultationData.date}
                        </span>
                      </div>
                    )}

                    {consultationData.time && (
                      <div className="flex justify-between text-sm">
                        <span className="text-muted-foreground">Time</span>
                        <span className="font-medium" data-testid="text-time">
                          {consultationData.time}
                        </span>
                      </div>
                    )}

                    {signupData.name && (
                      <div className="flex justify-between text-sm">
                        <span className="text-muted-foreground">Patient</span>
                        <span className="font-medium" data-testid="text-patient-name">
                          {signupData.name}
                        </span>
                      </div>
                    )}
                  </div>

                  <div className="border-t pt-4 space-y-3">
                    {plan ? (
                      <>
                        <div className="flex justify-between text-sm">
                          <span className="text-muted-foreground">Plan Price</span>
                          <span className="font-mono font-medium" data-testid="text-plan-price">
                            {parseFloat(plan.listPrice).toLocaleString('en-IN')}
                          </span>
                        </div>
                        <div className="flex justify-between text-sm">
                          <span className="text-emerald-600">Consultation Credit</span>
                          <span className="font-mono font-medium text-emerald-600" data-testid="text-consultation-credit">
                            -{parseFloat(plan.consultationFeeCredited).toLocaleString('en-IN')}
                          </span>
                        </div>
                        <div className="border-t pt-3">
                          <div className="flex justify-between items-center">
                            <span className="font-semibold text-lg">Final Payable</span>
                            <span className="font-mono text-2xl font-bold" data-testid="text-final-payable">
                              {parseFloat(plan.finalPayable).toLocaleString('en-IN')}
                            </span>
                          </div>
                        </div>
                      </>
                    ) : (
                      <div className="flex justify-center py-4">
                        <Loader2 className="h-6 w-6 animate-spin text-muted-foreground" />
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
}

client/src/pages/LabTechnicianPanel.tsx.content:
import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { ObjectUploader } from "@/components/ObjectUploader";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { FileText, Upload, CheckCircle, Clock, AlertCircle, Microscope } from "lucide-react";

type Customer = {
  id: string;
  email: string;
  name: string | null;
  role: string;
};

type StageProgress = {
  id: number;
  userId: string;
  stage: number;
  status: 'pending' | 'in_progress' | 'completed';
  startedAt: Date | null;
  completedAt: Date | null;
};

type Document = {
  id: number;
  userId: string;
  stage: number;
  label: string;
  url: string;
  uploadedByRole: string;
  uploadedAt: Date;
};

type Acknowledgement = {
  id: string;
  staffId: string;
  customerId: string;
  taskType: string;
  stage: number | null;
  status: 'pending' | 'acknowledged' | 'completed';
  acknowledgedAt: Date | null;
};

export default function LabTechnicianPanel() {
  const { toast } = useToast();
  const [selectedCustomer, setSelectedCustomer] = useState<Customer | null>(null);
  const [uploadLabel, setUploadLabel] = useState("");
  const reminderShown = useRef(false);

  // Fetch all customers
  const { data: customers = [], isLoading: loadingCustomers } = useQuery<Customer[]>({
    queryKey: ['/api/lab/customers']
  });

  // Fetch customer stage progress
  const { data: stageProgresses = [] } = useQuery<StageProgress[]>({
    queryKey: ['/api/user/stage-progress', selectedCustomer?.id],
    enabled: !!selectedCustomer
  });

  // Fetch customer documents
  const { data: documents = [] } = useQuery<Document[]>({
    queryKey: ['/api/user/documents', selectedCustomer?.id],
    enabled: !!selectedCustomer
  });

  // Fetch staff acknowledgements
  const { data: acknowledgements = [] } = useQuery<Acknowledgement[]>({
    queryKey: ['/api/acknowledgements/staff']
  });

  // Show reminder for pending acknowledgements
  useEffect(() => {
    if (!reminderShown.current && acknowledgements.length > 0) {
      const pendingCount = acknowledgements.filter(ack => ack.status === 'pending').length;
      if (pendingCount > 0) {
        toast({
          title: "Pending Tasks",
          description: `You have ${pendingCount} pending acknowledgement${pendingCount > 1 ? 's' : ''} to review`,
        });
        reminderShown.current = true;
      }
    }
  }, [acknowledgements, toast]);

  // Upload report mutation
  const uploadMutation = useMutation({
    mutationFn: async (data: { userId: string; stage: number; label: string; url: string }) => {
      return apiRequest('POST', '/api/lab/upload-report', data);
    },
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/user/stage-progress', variables.userId] });
      queryClient.invalidateQueries({ queryKey: ['/api/user/documents', variables.userId] });
      queryClient.invalidateQueries({ queryKey: ['/api/acknowledgements/staff'] });
      toast({
        title: "Success",
        description: "Test report uploaded successfully"
      });
      setUploadLabel("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to upload test report",
        variant: "destructive"
      });
    }
  });

  // Update acknowledgement mutation
  const acknowledgeMutation = useMutation({
    mutationFn: async (data: { id: string; status: string }) => {
      return apiRequest('PATCH', `/api/acknowledgements/${data.id}`, { status: data.status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/acknowledgements/staff'] });
      toast({
        title: "Success",
        description: "Task acknowledged successfully"
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to acknowledge task",
        variant: "destructive"
      });
    }
  });

  const getUploadParams = async () => {
    const res = await apiRequest('POST', '/api/objects/upload');
    const data = await res.json();
    return {
      method: "PUT" as const,
      url: data.url
    };
  };

  const handleUploadComplete = (result: any) => {
    if (!selectedCustomer || !uploadLabel.trim()) {
      toast({
        title: "Error",
        description: "Please enter a label for the upload",
        variant: "destructive"
      });
      return;
    }

    const uploadedFile = result.successful?.[0];
    if (!uploadedFile) {
      toast({
        title: "Error",
        description: "Upload failed",
        variant: "destructive"
      });
      return;
    }

    // Extract object path from presigned URL
    const urlObj = new URL(uploadedFile.uploadURL);
    const objectPath = urlObj.pathname.substring(1); // Remove leading /

    uploadMutation.mutate({
      userId: selectedCustomer.id,
      stage: 2, // Lab technician only uploads for stage 2
      label: uploadLabel,
      url: objectPath
    });
  };

  const getStageStatus = (stage: number): 'pending' | 'in_progress' | 'completed' => {
    const progress = stageProgresses.find(p => p.stage === stage);
    return progress?.status || 'pending';
  };

  const getPendingAcknowledgements = () => {
    return acknowledgements.filter(ack => ack.status === 'pending');
  };

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold flex items-center gap-2">
              <Microscope className="h-8 w-8" />
              Lab Technician Panel
            </h1>
            <p className="text-muted-foreground">Manage test collections and upload lab reports</p>
          </div>
          <Badge variant="outline" className="text-lg px-4 py-2" data-testid="badge-role">
            Lab Technician
          </Badge>
        </div>

        {getPendingAcknowledgements().length > 0 && (
          <Card className="border-yellow-500/50">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <AlertCircle className="h-5 w-5 text-yellow-500" />
                Pending Acknowledgements ({getPendingAcknowledgements().length})
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-2">
              {getPendingAcknowledgements().map(ack => (
                <div key={ack.id} className="flex items-center justify-between p-3 bg-muted rounded-md" data-testid={`acknowledgement-${ack.id}`}>
                  <div>
                    <p className="font-medium">{ack.taskType}</p>
                    <p className="text-sm text-muted-foreground">
                      {ack.stage ? `Stage ${ack.stage}` : 'General'} - Customer: {ack.customerId}
                    </p>
                  </div>
                  <Button
                    size="sm"
                    onClick={() => acknowledgeMutation.mutate({
                      id: ack.id,
                      status: 'acknowledged'
                    })}
                    disabled={acknowledgeMutation.isPending}
                    data-testid={`button-acknowledge-${ack.id}`}
                  >
                    <CheckCircle className="h-4 w-4 mr-2" />
                    Acknowledge
                  </Button>
                </div>
              ))}
            </CardContent>
          </Card>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Customer List */}
          <Card>
            <CardHeader>
              <CardTitle>Test Queue ({customers.length})</CardTitle>
            </CardHeader>
            <CardContent>
              {loadingCustomers ? (
                <div className="flex items-center justify-center py-8">
                  <Clock className="h-6 w-6 animate-spin" />
                </div>
              ) : (
                <div className="space-y-2 max-h-[600px] overflow-y-auto">
                  {customers.map(customer => (
                    <button
                      key={customer.id}
                      onClick={() => setSelectedCustomer(customer)}
                      className={`w-full text-left p-3 rounded-md transition-colors ${
                        selectedCustomer?.id === customer.id
                          ? 'bg-primary text-primary-foreground'
                          : 'hover-elevate active-elevate-2'
                      }`}
                      data-testid={`button-customer-${customer.id}`}
                    >
                      <p className="font-medium">{customer.name || 'Unnamed'}</p>
                      <p className="text-sm opacity-80">{customer.email}</p>
                    </button>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Patient Details & Actions */}
          <div className="lg:col-span-2 space-y-6">
            {!selectedCustomer ? (
              <Card>
                <CardContent className="py-12 text-center">
                  <p className="text-muted-foreground">Select a customer to view test details</p>
                </CardContent>
              </Card>
            ) : (
              <Tabs defaultValue="overview" className="space-y-4">
                <TabsList className="grid w-full grid-cols-3">
                  <TabsTrigger value="overview" data-testid="tab-overview">Overview</TabsTrigger>
                  <TabsTrigger value="upload" data-testid="tab-upload">Upload Test Report</TabsTrigger>
                  <TabsTrigger value="documents" data-testid="tab-documents">Test History</TabsTrigger>
                </TabsList>

                <TabsContent value="overview" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle>Patient Information</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-4">
                      <div>
                        <Label>Name</Label>
                        <p className="text-lg font-medium" data-testid="text-customer-name">{selectedCustomer.name || 'Not provided'}</p>
                      </div>
                      <div>
                        <Label>Email</Label>
                        <p className="text-lg" data-testid="text-customer-email">{selectedCustomer.email}</p>
                      </div>
                      <div>
                        <Label>Customer ID</Label>
                        <p className="text-sm font-mono" data-testid="text-customer-id">{selectedCustomer.id}</p>
                      </div>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardHeader>
                      <CardTitle>Stage Progress</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-3">
                      {[1, 2, 3, 4, 5, 6].map(stage => {
                        const status = getStageStatus(stage);
                        const stageName = ['Consultation', 'Test Collection', 'Discussion', 'Diet Chart', 'Payment', 'Delivery'][stage - 1];
                        const isLabStage = stage === 2;
                        return (
                          <div key={stage} className={`flex items-center justify-between p-2 rounded-md ${isLabStage ? 'bg-blue-500/10 border border-blue-500/20' : 'bg-muted'}`} data-testid={`stage-${stage}`}>
                            <div className="flex items-center gap-3">
                              <div className={`h-8 w-8 rounded-full flex items-center justify-center ${
                                status === 'completed' ? 'bg-green-500' :
                                status === 'in_progress' ? 'bg-blue-500' :
                                'bg-gray-500'
                              }`}>
                                <span className="text-white font-semibold">{stage}</span>
                              </div>
                              <div>
                                <p className="font-medium">{stageName} {isLabStage && '(Your Stage)'}</p>
                              </div>
                            </div>
                            <Badge variant={
                              status === 'completed' ? 'default' :
                              status === 'in_progress' ? 'secondary' :
                              'outline'
                            } data-testid={`badge-status-${stage}`}>
                              {status === 'completed' ? 'Completed' :
                               status === 'in_progress' ? 'In Progress' :
                               'Pending'}
                            </Badge>
                          </div>
                        );
                      })}
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="upload" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle>Upload Test Report (Stage 2)</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-4">
                      <div className="p-4 bg-blue-500/10 border border-blue-500/20 rounded-md">
                        <p className="text-sm font-medium">Stage 2: Test Collection</p>
                        <p className="text-sm text-muted-foreground mt-1">
                          Upload lab test results and diagnostic reports for the patient.
                        </p>
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="upload-label">Report Label</Label>
                        <Input
                          id="upload-label"
                          placeholder="e.g., Blood Work Results, X-Ray Report"
                          value={uploadLabel}
                          onChange={(e) => setUploadLabel(e.target.value)}
                          data-testid="input-label"
                        />
                      </div>

                      <div className="space-y-2">
                        <Label>Upload File</Label>
                        <ObjectUploader
                          onGetUploadParameters={getUploadParams}
                          onComplete={handleUploadComplete}
                          maxFileSize={10 * 1024 * 1024}
                          buttonVariant="outline"
                        >
                          <Upload className="h-4 w-4 mr-2" />
                          Choose File
                        </ObjectUploader>
                      </div>

                      <div className="flex items-center gap-2 text-sm text-muted-foreground">
                        <FileText className="h-4 w-4" />
                        <p>Supported formats: PDF, JPG, PNG (max 10MB)</p>
                      </div>
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="documents" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle>Test History ({documents.length})</CardTitle>
                    </CardHeader>
                    <CardContent>
                      {documents.length === 0 ? (
                        <p className="text-center text-muted-foreground py-8">No test reports uploaded yet</p>
                      ) : (
                        <div className="space-y-3">
                          {documents.map(doc => (
                            <div key={doc.id} className="flex items-center justify-between p-3 bg-muted rounded-md" data-testid={`document-${doc.id}`}>
                              <div className="flex items-center gap-3">
                                <FileText className="h-5 w-5 text-muted-foreground" />
                                <div>
                                  <p className="font-medium">{doc.label}</p>
                                  <p className="text-sm text-muted-foreground">
                                    Stage {doc.stage} - Uploaded by {doc.uploadedByRole}
                                  </p>
                                </div>
                              </div>
                              <Button variant="outline" size="sm" asChild data-testid={`button-download-${doc.id}`}>
                                <a href={`/objects/${doc.url}`} download>
                                  Download
                                </a>
                              </Button>
                            </div>
                          ))}
                        </div>
                      )}
                    </CardContent>
                  </Card>
                </TabsContent>
              </Tabs>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

