import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { ObjectUploader } from "@/components/ObjectUploader";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { FileText, Upload, CheckCircle, Clock, AlertCircle } from "lucide-react";

type Customer = {
  id: string;
  email: string;
  name: string | null;
  role: string;
};

type StageProgress = {
  id: number;
  userId: string;
  stage: number;
  status: 'pending' | 'in_progress' | 'completed';
  startedAt: Date | null;
  completedAt: Date | null;
};

type Document = {
  id: number;
  userId: string;
  stage: number;
  label: string;
  url: string;
  uploadedByRole: string;
  uploadedAt: Date;
};

type Acknowledgement = {
  id: string;
  staffId: string;
  customerId: string;
  taskType: string;
  stage: number | null;
  status: 'pending' | 'acknowledged' | 'completed';
  acknowledgedAt: Date | null;
};

export default function ConsultantPanel() {
  const { toast } = useToast();
  const [selectedCustomer, setSelectedCustomer] = useState<Customer | null>(null);
  const [uploadStage, setUploadStage] = useState<1 | 3>(1);
  const [uploadLabel, setUploadLabel] = useState("");
  const reminderShown = useRef(false);

  // Fetch all customers
  const { data: customers = [], isLoading: loadingCustomers } = useQuery<Customer[]>({
    queryKey: ['/api/consultant/customers']
  });

  // Fetch customer stage progress
  const { data: stageProgresses = [] } = useQuery<StageProgress[]>({
    queryKey: ['/api/user/stage-progress', selectedCustomer?.id],
    enabled: !!selectedCustomer
  });

  // Fetch customer documents
  const { data: documents = [] } = useQuery<Document[]>({
    queryKey: ['/api/user/documents', selectedCustomer?.id],
    enabled: !!selectedCustomer
  });

  // Fetch staff acknowledgements
  const { data: acknowledgements = [] } = useQuery<Acknowledgement[]>({
    queryKey: ['/api/acknowledgements/staff']
  });

  // Show reminder for pending acknowledgements
  useEffect(() => {
    if (!reminderShown.current && acknowledgements.length > 0) {
      const pendingCount = acknowledgements.filter(ack => ack.status === 'pending').length;
      if (pendingCount > 0) {
        toast({
          title: "Pending Tasks",
          description: `You have ${pendingCount} pending acknowledgement${pendingCount > 1 ? 's' : ''} to review`,
        });
        reminderShown.current = true;
      }
    }
  }, [acknowledgements, toast]);

  // Upload report mutation
  const uploadMutation = useMutation({
    mutationFn: async (data: { userId: string; stage: number; label: string; url: string }) => {
      return apiRequest('POST', '/api/consultant/upload-report', data);
    },
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/user/stage-progress', variables.userId] });
      queryClient.invalidateQueries({ queryKey: ['/api/user/documents', variables.userId] });
      queryClient.invalidateQueries({ queryKey: ['/api/acknowledgements/staff'] });
      toast({
        title: "Success",
        description: "Report uploaded successfully"
      });
      setUploadLabel("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to upload report",
        variant: "destructive"
      });
    }
  });

  // Update acknowledgement mutation
  const acknowledgeMutation = useMutation({
    mutationFn: async (data: { id: string; status: string }) => {
      return apiRequest('PATCH', `/api/acknowledgements/${data.id}`, { status: data.status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/acknowledgements/staff'] });
      toast({
        title: "Success",
        description: "Task acknowledged successfully"
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to acknowledge task",
        variant: "destructive"
      });
    }
  });

  const getUploadParams = async () => {
    const res = await apiRequest('POST', '/api/objects/upload');
    const data = await res.json();
    return {
      method: "PUT" as const,
      url: data.url
    };
  };

  const handleUploadComplete = (result: any) => {
    if (!selectedCustomer || !uploadLabel.trim()) {
      toast({
        title: "Error",
        description: "Please enter a label for the upload",
        variant: "destructive"
      });
      return;
    }

    const uploadedFile = result.successful?.[0];
    if (!uploadedFile) {
      toast({
        title: "Error",
        description: "Upload failed",
        variant: "destructive"
      });
      return;
    }

    // Extract object path from presigned URL
    const urlObj = new URL(uploadedFile.uploadURL);
    const objectPath = urlObj.pathname.substring(1); // Remove leading /

    uploadMutation.mutate({
      userId: selectedCustomer.id,
      stage: uploadStage,
      label: uploadLabel,
      url: objectPath
    });
  };

  const getStageStatus = (stage: number): 'pending' | 'in_progress' | 'completed' => {
    const progress = stageProgresses.find(p => p.stage === stage);
    return progress?.status || 'pending';
  };

  const getPendingAcknowledgements = () => {
    return acknowledgements.filter(ack => ack.status === 'pending');
  };

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold">Consultant Panel</h1>
            <p className="text-muted-foreground">Manage patient consultations and medical reports</p>
          </div>
          <Badge variant="outline" className="text-lg px-4 py-2" data-testid="badge-role">
            Consultant
          </Badge>
        </div>

        {getPendingAcknowledgements().length > 0 && (
          <Card className="border-yellow-500/50">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <AlertCircle className="h-5 w-5 text-yellow-500" />
                Pending Acknowledgements ({getPendingAcknowledgements().length})
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-2">
              {getPendingAcknowledgements().map(ack => (
                <div key={ack.id} className="flex items-center justify-between p-3 bg-muted rounded-md" data-testid={`acknowledgement-${ack.id}`}>
                  <div>
                    <p className="font-medium">{ack.taskType}</p>
                    <p className="text-sm text-muted-foreground">
                      {ack.stage ? `Stage ${ack.stage}` : 'General'} - Customer: {ack.customerId}
                    </p>
                  </div>
                  <Button
                    size="sm"
                    onClick={() => acknowledgeMutation.mutate({
                      id: ack.id,
                      status: 'acknowledged'
                    })}
                    disabled={acknowledgeMutation.isPending}
                    data-testid={`button-acknowledge-${ack.id}`}
                  >
                    <CheckCircle className="h-4 w-4 mr-2" />
                    Acknowledge
                  </Button>
                </div>
              ))}
            </CardContent>
          </Card>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Customer List */}
          <Card>
            <CardHeader>
              <CardTitle>Customers ({customers.length})</CardTitle>
            </CardHeader>
            <CardContent>
              {loadingCustomers ? (
                <div className="flex items-center justify-center py-8">
                  <Clock className="h-6 w-6 animate-spin" />
                </div>
              ) : (
                <div className="space-y-2 max-h-[600px] overflow-y-auto">
                  {customers.map(customer => (
                    <button
                      key={customer.id}
                      onClick={() => setSelectedCustomer(customer)}
                      className={`w-full text-left p-3 rounded-md transition-colors ${
                        selectedCustomer?.id === customer.id
                          ? 'bg-primary text-primary-foreground'
                          : 'hover-elevate active-elevate-2'
                      }`}
                      data-testid={`button-customer-${customer.id}`}
                    >
                      <p className="font-medium">{customer.name || 'Unnamed'}</p>
                      <p className="text-sm opacity-80">{customer.email}</p>
                    </button>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Patient Details & Actions */}
          <div className="lg:col-span-2 space-y-6">
            {!selectedCustomer ? (
              <Card>
                <CardContent className="py-12 text-center">
                  <p className="text-muted-foreground">Select a customer to view details</p>
                </CardContent>
              </Card>
            ) : (
              <Tabs defaultValue="overview" className="space-y-4">
                <TabsList className="grid w-full grid-cols-3">
                  <TabsTrigger value="overview" data-testid="tab-overview">Overview</TabsTrigger>
                  <TabsTrigger value="upload" data-testid="tab-upload">Upload Reports</TabsTrigger>
                  <TabsTrigger value="documents" data-testid="tab-documents">Documents</TabsTrigger>
                </TabsList>

                <TabsContent value="overview" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle>Patient Information</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-4">
                      <div>
                        <Label>Name</Label>
                        <p className="text-lg font-medium" data-testid="text-customer-name">{selectedCustomer.name || 'Not provided'}</p>
                      </div>
                      <div>
                        <Label>Email</Label>
                        <p className="text-lg" data-testid="text-customer-email">{selectedCustomer.email}</p>
                      </div>
                      <div>
                        <Label>Customer ID</Label>
                        <p className="text-sm font-mono" data-testid="text-customer-id">{selectedCustomer.id}</p>
                      </div>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardHeader>
                      <CardTitle>Stage Progress</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-3">
                      {[1, 2, 3, 4, 5, 6].map(stage => {
                        const status = getStageStatus(stage);
                        const stageName = ['Consultation', 'Test Collection', 'Discussion', 'Diet Chart', 'Payment', 'Delivery'][stage - 1];
                        return (
                          <div key={stage} className="flex items-center justify-between p-2 rounded-md bg-muted" data-testid={`stage-${stage}`}>
                            <div className="flex items-center gap-3">
                              <div className={`h-8 w-8 rounded-full flex items-center justify-center ${
                                status === 'completed' ? 'bg-green-500' :
                                status === 'in_progress' ? 'bg-blue-500' :
                                'bg-gray-500'
                              }`}>
                                <span className="text-white font-semibold">{stage}</span>
                              </div>
                              <div>
                                <p className="font-medium">{stageName}</p>
                              </div>
                            </div>
                            <Badge variant={
                              status === 'completed' ? 'default' :
                              status === 'in_progress' ? 'secondary' :
                              'outline'
                            } data-testid={`badge-status-${stage}`}>
                              {status === 'completed' ? 'Completed' :
                               status === 'in_progress' ? 'In Progress' :
                               'Pending'}
                            </Badge>
                          </div>
                        );
                      })}
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="upload" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle>Upload Medical Reports</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-4">
                      <div className="space-y-2">
                        <Label>Select Stage</Label>
                        <div className="flex gap-2">
                          <Button
                            variant={uploadStage === 1 ? 'default' : 'outline'}
                            onClick={() => setUploadStage(1)}
                            data-testid="button-stage-1"
                          >
                            Stage 1 - Initial Consultation
                          </Button>
                          <Button
                            variant={uploadStage === 3 ? 'default' : 'outline'}
                            onClick={() => setUploadStage(3)}
                            data-testid="button-stage-3"
                          >
                            Stage 3 - Discussion Report
                          </Button>
                        </div>
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="upload-label">Report Label</Label>
                        <Input
                          id="upload-label"
                          placeholder="e.g., Initial Assessment, Follow-up Report"
                          value={uploadLabel}
                          onChange={(e) => setUploadLabel(e.target.value)}
                          data-testid="input-label"
                        />
                      </div>

                      <div className="space-y-2">
                        <Label>Upload File</Label>
                        <ObjectUploader
                          onGetUploadParameters={getUploadParams}
                          onComplete={handleUploadComplete}
                          maxFileSize={10 * 1024 * 1024}
                          buttonVariant="outline"
                        >
                          <Upload className="h-4 w-4 mr-2" />
                          Choose File
                        </ObjectUploader>
                      </div>

                      <div className="flex items-center gap-2 text-sm text-muted-foreground">
                        <FileText className="h-4 w-4" />
                        <p>Supported formats: PDF, JPG, PNG (max 10MB)</p>
                      </div>
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="documents" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle>Uploaded Documents ({documents.length})</CardTitle>
                    </CardHeader>
                    <CardContent>
                      {documents.length === 0 ? (
                        <p className="text-center text-muted-foreground py-8">No documents uploaded yet</p>
                      ) : (
                        <div className="space-y-3">
                          {documents.map(doc => (
                            <div key={doc.id} className="flex items-center justify-between p-3 bg-muted rounded-md" data-testid={`document-${doc.id}`}>
                              <div className="flex items-center gap-3">
                                <FileText className="h-5 w-5 text-muted-foreground" />
                                <div>
                                  <p className="font-medium">{doc.label}</p>
                                  <p className="text-sm text-muted-foreground">
                                    Stage {doc.stage} - Uploaded by {doc.uploadedByRole}
                                  </p>
                                </div>
                              </div>
                              <Button variant="outline" size="sm" asChild data-testid={`button-download-${doc.id}`}>
                                <a href={`/objects/${doc.url}`} download>
                                  Download
                                </a>
                              </Button>
                            </div>
                          ))}
                        </div>
                      )}
                    </CardContent>
                  </Card>
                </TabsContent>
              </Tabs>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/ChefPanel.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { ChefHat, Clock, CheckCircle, Calendar } from "lucide-react";

type Customer = {
  id: string;
  email: string;
  name: string | null;
  role: string;
};

type Plan = {
  id: number;
  userId: string;
  planType: string;
  isActive: boolean;
  startDate: Date;
  endDate: Date | null;
};

type DietPlan = {
  id: number;
  userId: string;
  macros: string | null;
  weeklyPlan: string | null;
};

type Address = {
  id: number;
  userId: string;
  addressLine1: string;
  addressLine2: string | null;
  city: string;
  state: string;
  zipCode: string;
};

type ActivePlan = {
  customer: Customer;
  plan: Plan | null;
  addresses: Address[];
  dietPlan: DietPlan | null;
};

type StaffActivity = {
  id: number;
  staffId: string;
  customerId: string;
  actionType: string;
  stage: number | null;
  description: string;
  metadata: any;
  createdAt: Date;
};

export default function ChefPanel() {
  const { toast } = useToast();
  const [selectedPlan, setSelectedPlan] = useState<ActivePlan | null>(null);
  const [selectedDate, setSelectedDate] = useState(() => {
    const today = new Date();
    return today.toISOString().split('T')[0];
  });
  const [selectedMealType, setSelectedMealType] = useState<'breakfast' | 'lunch' | 'dinner'>('breakfast');

  // Fetch active plans
  const { data: activePlans = [], isLoading: loadingPlans } = useQuery<ActivePlan[]>({
    queryKey: ['/api/chef/active-plans']
  });

  // Fetch my preparation activity
  const { data: myActivity = [] } = useQuery<StaffActivity[]>({
    queryKey: ['/api/activity/staff']
  });

  // Mark meal as prepared mutation
  const markPreparedMutation = useMutation({
    mutationFn: async (data: { userId: string; mealType: string; date: string }) => {
      return apiRequest('POST', '/api/chef/mark-prepared', data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/activity/staff'] });
      toast({
        title: "Success",
        description: `${selectedMealType.charAt(0).toUpperCase() + selectedMealType.slice(1)} marked as prepared`
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to mark meal as prepared",
        variant: "destructive"
      });
    }
  });

  const handleMarkPrepared = () => {
    if (!selectedPlan?.customer) return;

    markPreparedMutation.mutate({
      userId: selectedPlan.customer.id,
      mealType: selectedMealType,
      date: selectedDate
    });
  };

  const getMealPreparations = () => {
    return myActivity.filter(a => a.actionType === 'meal_prepared');
  };

  const getTodayPreparations = () => {
    const today = new Date().toISOString().split('T')[0];
    return getMealPreparations().filter(a => {
      const metadata = a.metadata as { date?: string };
      return metadata.date === today;
    });
  };

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold flex items-center gap-2">
              <ChefHat className="h-8 w-8" />
              Chef Panel
            </h1>
            <p className="text-muted-foreground">Prepare meals for active diet plans</p>
          </div>
          <Badge variant="outline" className="text-lg px-4 py-2" data-testid="badge-role">
            Chef
          </Badge>
        </div>

        <Card className="border-blue-500/50">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <CheckCircle className="h-5 w-5 text-blue-500" />
              Today's Preparations ({getTodayPreparations().length})
            </CardTitle>
          </CardHeader>
          <CardContent>
            {getTodayPreparations().length === 0 ? (
              <p className="text-center text-muted-foreground py-4">No meals prepared today yet</p>
            ) : (
              <div className="space-y-2">
                {getTodayPreparations().map(prep => {
                  const metadata = prep.metadata as { mealType?: string; date?: string };
                  return (
                    <div key={prep.id} className="flex items-center justify-between p-2 bg-muted rounded-md" data-testid={`prep-${prep.id}`}>
                      <div>
                        <p className="font-medium capitalize">{metadata.mealType}</p>
                        <p className="text-sm text-muted-foreground">Customer: {prep.customerId}</p>
                      </div>
                      <Badge variant="default">Prepared</Badge>
                    </div>
                  );
                })}
              </div>
            )}
          </CardContent>
        </Card>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Active Plans List */}
          <Card>
            <CardHeader>
              <CardTitle>Active Plans ({activePlans.length})</CardTitle>
            </CardHeader>
            <CardContent>
              {loadingPlans ? (
                <div className="flex items-center justify-center py-8">
                  <Clock className="h-6 w-6 animate-spin" />
                </div>
              ) : (
                <div className="space-y-2 max-h-[600px] overflow-y-auto">
                  {activePlans.length === 0 ? (
                    <p className="text-center text-muted-foreground py-4">No active plans</p>
                  ) : (
                    activePlans.map((plan) => (
                      <button
                        key={plan.customer.id}
                        onClick={() => setSelectedPlan(plan)}
                        className={`w-full text-left p-3 rounded-md transition-colors ${
                          selectedPlan?.customer.id === plan.customer.id
                            ? 'bg-primary text-primary-foreground'
                            : 'hover-elevate active-elevate-2'
                        }`}
                        data-testid={`button-plan-${plan.customer.id}`}
                      >
                        <p className="font-medium">{plan.customer.name || 'Unnamed'}</p>
                        <p className="text-sm opacity-80">{plan.plan?.planType || 'No plan type'}</p>
                      </button>
                    ))
                  )}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Plan Details & Preparation */}
          <div className="lg:col-span-2 space-y-6">
            {!selectedPlan ? (
              <Card>
                <CardContent className="py-12 text-center">
                  <p className="text-muted-foreground">Select an active plan to prepare meals</p>
                </CardContent>
              </Card>
            ) : (
              <Tabs defaultValue="diet" className="space-y-4">
                <TabsList className="grid w-full grid-cols-3">
                  <TabsTrigger value="diet" data-testid="tab-diet">Diet Plan</TabsTrigger>
                  <TabsTrigger value="prepare" data-testid="tab-prepare">Prepare Meal</TabsTrigger>
                  <TabsTrigger value="history" data-testid="tab-history">Preparation History</TabsTrigger>
                </TabsList>

                <TabsContent value="diet" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle>Customer Information</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-4">
                      <div>
                        <label className="text-sm font-medium text-muted-foreground">Name</label>
                        <p className="text-lg font-medium" data-testid="text-customer-name">{selectedPlan.customer.name || 'Not provided'}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-muted-foreground">Email</label>
                        <p className="text-lg" data-testid="text-customer-email">{selectedPlan.customer.email}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-muted-foreground">Plan Type</label>
                        <Badge variant="outline" className="text-base" data-testid="text-plan-type">{selectedPlan.plan?.planType || 'Unknown'}</Badge>
                      </div>
                    </CardContent>
                  </Card>

                  {selectedPlan.dietPlan && (
                    <Card>
                      <CardHeader>
                        <CardTitle>Diet Plan Details</CardTitle>
                      </CardHeader>
                      <CardContent className="space-y-4">
                        {selectedPlan.dietPlan.macros && (
                          <div>
                            <label className="text-sm font-medium text-muted-foreground">Macros</label>
                            <p className="mt-1 whitespace-pre-wrap" data-testid="text-macros">{selectedPlan.dietPlan.macros}</p>
                          </div>
                        )}
                        {selectedPlan.dietPlan.weeklyPlan && (
                          <div>
                            <label className="text-sm font-medium text-muted-foreground">Weekly Plan</label>
                            <p className="mt-1 whitespace-pre-wrap" data-testid="text-weekly-plan">{selectedPlan.dietPlan.weeklyPlan}</p>
                          </div>
                        )}
                        {!selectedPlan.dietPlan.macros && !selectedPlan.dietPlan.weeklyPlan && (
                          <p className="text-center text-muted-foreground">No diet plan details available</p>
                        )}
                      </CardContent>
                    </Card>
                  )}

                  {selectedPlan.addresses.length > 0 && (
                    <Card>
                      <CardHeader>
                        <CardTitle>Delivery Address</CardTitle>
                      </CardHeader>
                      <CardContent>
                        <div className="space-y-2">
                          {selectedPlan.addresses.map(addr => (
                            <div key={addr.id} className="p-3 bg-muted rounded-md" data-testid={`address-${addr.id}`}>
                              <p className="font-medium">{addr.addressLine1}</p>
                              {addr.addressLine2 && <p className="text-sm">{addr.addressLine2}</p>}
                              <p className="text-sm text-muted-foreground">{addr.city}, {addr.state} {addr.zipCode}</p>
                            </div>
                          ))}
                        </div>
                      </CardContent>
                    </Card>
                  )}
                </TabsContent>

                <TabsContent value="prepare" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle>Mark Meal as Prepared</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-6">
                      <div className="space-y-2">
                        <label className="text-sm font-medium">Date</label>
                        <div className="flex items-center gap-2">
                          <Calendar className="h-4 w-4 text-muted-foreground" />
                          <input
                            type="date"
                            value={selectedDate}
                            onChange={(e) => setSelectedDate(e.target.value)}
                            className="flex h-9 w-full rounded-md border border-input bg-transparent px-3 py-1 text-sm shadow-sm transition-colors focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring"
                            data-testid="input-date"
                          />
                        </div>
                      </div>

                      <div className="space-y-2">
                        <label className="text-sm font-medium">Meal Type</label>
                        <div className="grid grid-cols-3 gap-2">
                          {(['breakfast', 'lunch', 'dinner'] as const).map(mealType => (
                            <button
                              key={mealType}
                              onClick={() => setSelectedMealType(mealType)}
                              className={`p-3 rounded-md capitalize transition-colors ${
                                selectedMealType === mealType
                                  ? 'bg-primary text-primary-foreground'
                                  : 'bg-muted hover-elevate active-elevate-2'
                              }`}
                              data-testid={`button-meal-${mealType}`}
                            >
                              {mealType}
                            </button>
                          ))}
                        </div>
                      </div>

                      <Button
                        onClick={handleMarkPrepared}
                        disabled={markPreparedMutation.isPending}
                        className="w-full"
                        size="lg"
                        data-testid="button-mark-prepared"
                      >
                        <CheckCircle className="h-4 w-4 mr-2" />
                        Mark as Prepared
                      </Button>
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="history" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle>All Preparations ({getMealPreparations().length})</CardTitle>
                    </CardHeader>
                    <CardContent>
                      {getMealPreparations().length === 0 ? (
                        <p className="text-center text-muted-foreground py-8">No preparation history</p>
                      ) : (
                        <div className="space-y-3 max-h-[500px] overflow-y-auto">
                          {getMealPreparations().map(prep => {
                            const metadata = prep.metadata as { mealType?: string; date?: string };
                            return (
                              <div key={prep.id} className="flex items-center justify-between p-3 bg-muted rounded-md" data-testid={`history-${prep.id}`}>
                                <div>
                                  <p className="font-medium capitalize">{metadata.mealType}</p>
                                  <p className="text-sm text-muted-foreground">
                                    {metadata.date} - {new Date(prep.createdAt).toLocaleString()}
                                  </p>
                                </div>
                                <Badge variant="default">Prepared</Badge>
                              </div>
                            );
                          })}
                        </div>
                      )}
                    </CardContent>
                  </Card>
                </TabsContent>
              </Tabs>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/DeliveryPanel.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Truck, MapPin, Package, CheckCircle, Navigation } from "lucide-react";

type Order = {
  id: number;
  userId: string;
  status: string;
  totalAmount: number;
  deliveryDate: Date;
  assignedDeliveryPersonId: string | null;
  createdAt: Date;
};

type StaffActivity = {
  id: number;
  staffId: string;
  customerId: string;
  actionType: string;
  stage: number | null;
  description: string;
  metadata: any;
  createdAt: Date;
};

type DeliveryLocation = {
  id: number;
  deliveryPersonId: string;
  latitude: number;
  longitude: number;
  status: string;
  updatedAt: Date;
};

export default function DeliveryPanel() {
  const { toast } = useToast();
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [gpsEnabled, setGpsEnabled] = useState(false);
  const [currentLocation, setCurrentLocation] = useState<{ latitude: number; longitude: number } | null>(null);

  // Fetch assigned deliveries
  const { data: assignedOrders = [], isLoading: loadingOrders } = useQuery<Order[]>({
    queryKey: ['/api/delivery/assigned']
  });

  // Fetch delivery activity logs
  const { data: activityLogs = [] } = useQuery<StaffActivity[]>({
    queryKey: ['/api/activity/staff']
  });

  // Update delivery status mutation
  const updateStatusMutation = useMutation({
    mutationFn: async (data: { orderId: number; status: string }) => {
      return apiRequest('PATCH', `/api/delivery/status/${data.orderId}`, { status: data.status });
    },
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/delivery/assigned'] });
      queryClient.invalidateQueries({ queryKey: ['/api/activity/staff'] });
      toast({
        title: "Success",
        description: "Delivery status updated successfully"
      });

      // Only clear selection if delivered (order removed from active list)
      if (variables.status === 'delivered') {
        setSelectedOrder(null);
      } else {
        // Update selected order status locally for immediate UI feedback
        if (selectedOrder) {
          setSelectedOrder({ ...selectedOrder, status: variables.status });
        }
      }
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update delivery status",
        variant: "destructive"
      });
    }
  });

  // Update location mutation
  const updateLocationMutation = useMutation({
    mutationFn: async (data: { latitude: number; longitude: number; status?: string }) => {
      return apiRequest('POST', '/api/delivery/location', data);
    },
    onSuccess: () => {
      toast({
        title: "Location Updated",
        description: "Your GPS location has been updated"
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update location",
        variant: "destructive"
      });
    }
  });

  // GPS tracking effect
  useEffect(() => {
    let watchId: number | null = null;

    if (gpsEnabled && 'geolocation' in navigator) {
      watchId = navigator.geolocation.watchPosition(
        (position) => {
          const location = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          };
          setCurrentLocation(location);

          // Update location every 2 minutes
          updateLocationMutation.mutate({
            ...location,
            status: assignedOrders.length > 0 ? 'on_delivery' : 'on_duty'
          });
        },
        (error) => {
          console.error("GPS Error:", error);
          toast({
            title: "GPS Error",
            description: "Unable to access location. Please enable location services.",
            variant: "destructive"
          });
          setGpsEnabled(false);
        },
        {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 120000 // 2 minutes
        }
      );
    }

    return () => {
      if (watchId !== null) {
        navigator.geolocation.clearWatch(watchId);
      }
    };
  }, [gpsEnabled, assignedOrders.length]);

  const handleStatusUpdate = (status: string) => {
    if (!selectedOrder) return;
    updateStatusMutation.mutate({
      orderId: selectedOrder.id,
      status
    });
  };

  const getDeliveryLogs = () => {
    return activityLogs.filter(log => log.actionType === 'delivery_completed');
  };

  const getTodayDeliveries = () => {
    const today = new Date().toISOString().split('T')[0];
    return getDeliveryLogs().filter(log => {
      const logDate = new Date(log.createdAt).toISOString().split('T')[0];
      return logDate === today;
    });
  };

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold flex items-center gap-2">
              <Truck className="h-8 w-8" />
              Delivery Panel
            </h1>
            <p className="text-muted-foreground">Manage deliveries and track locations</p>
          </div>
          <Badge variant="outline" className="text-lg px-4 py-2" data-testid="badge-role">
            Delivery
          </Badge>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <Card className="border-blue-500/50">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Package className="h-5 w-5 text-blue-500" />
                Today's Deliveries ({getTodayDeliveries().length})
              </CardTitle>
            </CardHeader>
            <CardContent>
              {getTodayDeliveries().length === 0 ? (
                <p className="text-center text-muted-foreground py-4">No deliveries completed today</p>
              ) : (
                <div className="space-y-2">
                  {getTodayDeliveries().map(log => {
                    const metadata = log.metadata as { orderId?: number; deliveredAt?: string };
                    return (
                      <div key={log.id} className="flex items-center justify-between p-2 bg-muted rounded-md" data-testid={`today-delivery-${log.id}`}>
                        <div>
                          <p className="font-medium">Order #{metadata.orderId}</p>
                          <p className="text-sm text-muted-foreground">{new Date(log.createdAt).toLocaleTimeString()}</p>
                        </div>
                        <Badge variant="default">Delivered</Badge>
                      </div>
                    );
                  })}
                </div>
              )}
            </CardContent>
          </Card>

          <Card className={gpsEnabled ? "border-green-500/50" : ""}>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <MapPin className="h-5 w-5" />
                GPS Tracking
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-sm font-medium">Location Services</span>
                <Button
                  onClick={() => setGpsEnabled(!gpsEnabled)}
                  variant={gpsEnabled ? "default" : "outline"}
                  size="sm"
                  data-testid="button-gps-toggle"
                >
                  <Navigation className="h-4 w-4 mr-2" />
                  {gpsEnabled ? 'Enabled' : 'Disabled'}
                </Button>
              </div>

              {gpsEnabled && currentLocation && (
                <div className="p-3 bg-muted rounded-md space-y-1" data-testid="gps-location">
                  <p className="text-sm font-medium">Current Location:</p>
                  <p className="text-xs text-muted-foreground">Lat: {currentLocation.latitude.toFixed(6)}</p>
                  <p className="text-xs text-muted-foreground">Lng: {currentLocation.longitude.toFixed(6)}</p>
                  <p className="text-xs text-green-600">Updates every 2 minutes</p>
                </div>
              )}

              {!gpsEnabled && (
                <p className="text-sm text-muted-foreground">Enable GPS to track your delivery location</p>
              )}
            </CardContent>
          </Card>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Assigned Deliveries List */}
          <Card>
            <CardHeader>
              <CardTitle>Assigned Deliveries ({assignedOrders.length})</CardTitle>
            </CardHeader>
            <CardContent>
              {loadingOrders ? (
                <div className="flex items-center justify-center py-8">
                  <Package className="h-6 w-6 animate-spin" />
                </div>
              ) : (
                <div className="space-y-2 max-h-[600px] overflow-y-auto">
                  {assignedOrders.length === 0 ? (
                    <p className="text-center text-muted-foreground py-4">No deliveries assigned</p>
                  ) : (
                    assignedOrders.map(order => (
                      <button
                        key={order.id}
                        onClick={() => setSelectedOrder(order)}
                        className={`w-full text-left p-3 rounded-md transition-colors ${
                          selectedOrder?.id === order.id
                            ? 'bg-primary text-primary-foreground'
                            : 'hover-elevate active-elevate-2'
                        }`}
                        data-testid={`button-order-${order.id}`}
                      >
                        <p className="font-medium">Order #{order.id}</p>
                        <p className="text-sm opacity-80">Amount: ${order.totalAmount}</p>
                        <Badge variant="outline" className="mt-1">{order.status}</Badge>
                      </button>
                    ))
                  )}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Delivery Actions & History */}
          <div className="lg:col-span-2 space-y-6">
            {!selectedOrder ? (
              <Card>
                <CardContent className="py-12 text-center">
                  <p className="text-muted-foreground">Select a delivery to update status</p>
                </CardContent>
              </Card>
            ) : (
              <Tabs defaultValue="update" className="space-y-4">
                <TabsList className="grid w-full grid-cols-2">
                  <TabsTrigger value="update" data-testid="tab-update">Update Status</TabsTrigger>
                  <TabsTrigger value="history" data-testid="tab-history">Delivery History</TabsTrigger>
                </TabsList>

                <TabsContent value="update" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle>Order #{selectedOrder.id}</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-4">
                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <label className="text-sm font-medium text-muted-foreground">Customer ID</label>
                          <p className="text-lg font-mono" data-testid="text-customer-id">{selectedOrder.userId}</p>
                        </div>
                        <div>
                          <label className="text-sm font-medium text-muted-foreground">Amount</label>
                          <p className="text-lg font-medium" data-testid="text-amount">${selectedOrder.totalAmount}</p>
                        </div>
                        <div>
                          <label className="text-sm font-medium text-muted-foreground">Status</label>
                          <Badge variant="outline" className="text-base" data-testid="text-status">{selectedOrder.status}</Badge>
                        </div>
                        <div>
                          <label className="text-sm font-medium text-muted-foreground">Delivery Date</label>
                          <p className="text-sm" data-testid="text-delivery-date">{new Date(selectedOrder.deliveryDate).toLocaleDateString()}</p>
                        </div>
                      </div>

                      <div className="border-t pt-4 space-y-3">
                        <h3 className="font-medium">Update Delivery Status</h3>

                        {selectedOrder.status === 'prepared' && (
                          <Button
                            onClick={() => handleStatusUpdate('in_transit')}
                            disabled={updateStatusMutation.isPending}
                            className="w-full"
                            data-testid="button-start-delivery"
                          >
                            <Truck className="h-4 w-4 mr-2" />
                            Start Delivery (In Transit)
                          </Button>
                        )}

                        {selectedOrder.status === 'in_transit' && (
                          <Button
                            onClick={() => handleStatusUpdate('delivered')}
                            disabled={updateStatusMutation.isPending}
                            className="w-full"
                            data-testid="button-mark-delivered"
                          >
                            <CheckCircle className="h-4 w-4 mr-2" />
                            Mark as Delivered
                          </Button>
                        )}

                        {selectedOrder.status === 'delivered' && (
                          <div className="p-4 bg-green-500/10 border border-green-500/20 rounded-md">
                            <p className="text-sm font-medium text-green-700 dark:text-green-400">
                               This order has been delivered
                            </p>
                          </div>
                        )}
                      </div>
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="history" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle>All Deliveries ({getDeliveryLogs().length})</CardTitle>
                    </CardHeader>
                    <CardContent>
                      {getDeliveryLogs().length === 0 ? (
                        <p className="text-center text-muted-foreground py-8">No delivery history</p>
                      ) : (
                        <div className="space-y-3 max-h-[500px] overflow-y-auto">
                          {getDeliveryLogs().map(log => {
                            const metadata = log.metadata as { orderId?: number; deliveredAt?: string };
                            return (
                              <div key={log.id} className="flex items-center justify-between p-3 bg-muted rounded-md" data-testid={`history-${log.id}`}>
                                <div>
                                  <p className="font-medium">Order #{metadata.orderId}</p>
                                  <p className="text-sm text-muted-foreground">
                                    {new Date(log.createdAt).toLocaleString()}
                                  </p>
                                </div>
                                <Badge variant="default">Delivered</Badge>
                              </div>
                            );
                          })}
                        </div>
                      )}
                    </CardContent>
                  </Card>
                </TabsContent>
              </Tabs>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

server/pdfGenerator.ts.content:
import PDFDocument from 'pdfkit';
import { Readable } from 'stream';

interface DietChartData {
  customerName: string;
  customerId: string;
  macros?: any;
  weeklyPlan?: any;
  nutritionistName?: string;
  generatedDate: Date;
}

interface ConsolidatedReportData {
  customerName: string;
  customerId: string;
  stages: {
    stage: number;
    status: string;
    completedAt?: Date;
    documents: Array<{
      label: string;
      url: string;
      uploadedAt: Date;
    }>;
  }[];
  dietPlan?: {
    macros?: any;
    weeklyPlan?: any;
  };
  acknowledgements?: Array<{
    taskType: string;
    status: string;
    createdAt: Date;
  }>;
}

export function generateDietChartPDF(data: DietChartData): Promise<Buffer> {
  return new Promise((resolve, reject) => {
    const doc = new PDFDocument({ margin: 50 });
    const chunks: Buffer[] = [];

    doc.on('data', (chunk) => chunks.push(chunk));
    doc.on('end', () => resolve(Buffer.concat(chunks)));
    doc.on('error', reject);

    // Header
    doc.fontSize(24).text('Personalized Diet Chart', { align: 'center' });
    doc.moveDown(0.5);
    doc.fontSize(10).text(`Generated on ${data.generatedDate.toLocaleDateString()}`, { align: 'center' });
    doc.moveDown(2);

    // Patient Information
    doc.fontSize(16).text('Patient Information', { underline: true });
    doc.moveDown(0.5);
    doc.fontSize(12).text(`Name: ${data.customerName}`);
    doc.text(`Patient ID: ${data.customerId}`);
    doc.moveDown(1.5);

    // Macros Section
    if (data.macros) {
      doc.fontSize(16).text('Nutritional Macros', { underline: true });
      doc.moveDown(0.5);

      if (typeof data.macros === 'object') {
        Object.entries(data.macros).forEach(([key, value]) => {
          doc.fontSize(12).text(`${key}: ${value}`);
        });
      } else {
        doc.fontSize(12).text(String(data.macros));
      }
      doc.moveDown(1.5);
    }

    // Weekly Plan Section
    if (data.weeklyPlan) {
      doc.fontSize(16).text('Weekly Meal Plan', { underline: true });
      doc.moveDown(0.5);

      if (typeof data.weeklyPlan === 'object' && data.weeklyPlan !== null) {
        Object.entries(data.weeklyPlan).forEach(([day, meals]) => {
          doc.fontSize(14).text(day, { underline: true });
          if (typeof meals === 'object' && meals !== null) {
            Object.entries(meals).forEach(([mealType, description]) => {
              doc.fontSize(12).text(`  ${mealType}: ${description}`);
            });
          } else {
            doc.fontSize(12).text(`  ${meals}`);
          }
          doc.moveDown(0.5);
        });
      } else {
        doc.fontSize(12).text(String(data.weeklyPlan));
      }
      doc.moveDown(1.5);
    }

    // Footer with nutritionist signature
    doc.moveDown(2);
    doc.fontSize(12).text('Prepared by:', { continued: false });
    doc.text(data.nutritionistName || 'Clinical Nutritionist');
    doc.moveDown(0.5);
    doc.fillColor('gray').fontSize(10).text('This diet plan is personalized based on your health profile and clinical assessment.', {
      align: 'center'
    });

    doc.end();
  });
}

export function generateConsolidatedReportPDF(data: ConsolidatedReportData): Promise<Buffer> {
  return new Promise((resolve, reject) => {
    const doc = new PDFDocument({ margin: 50 });
    const chunks: Buffer[] = [];

    doc.on('data', (chunk) => chunks.push(chunk));
    doc.on('end', () => resolve(Buffer.concat(chunks)));
    doc.on('error', reject);

    // Header
    doc.fontSize(24).text('Clinical Progress Report', { align: 'center' });
    doc.moveDown(0.5);
    doc.fontSize(10).text(`Generated on ${new Date().toLocaleDateString()}`, { align: 'center' });
    doc.moveDown(2);

    // Patient Information
    doc.fontSize(16).text('Patient Information', { underline: true });
    doc.moveDown(0.5);
    doc.fontSize(12).text(`Name: ${data.customerName}`);
    doc.text(`Patient ID: ${data.customerId}`);
    doc.moveDown(1.5);

    // Clinical Journey Summary
    doc.fontSize(16).text('Clinical Journey Summary', { underline: true });
    doc.moveDown(0.5);

    data.stages.forEach((stage) => {
      const stageNames = [
        'Initial Physician Consultation',
        'Test Collection',
        'Discussion',
        'Diet Chart',
        'Payment',
        'Meal Delivery'
      ];

      doc.fontSize(14).text(`Stage ${stage.stage}: ${stageNames[stage.stage - 1] || 'Unknown'}`, { underline: true });
      doc.fontSize(12).text(`Status: ${stage.status}`);
      if (stage.completedAt) {
        doc.text(`Completed: ${stage.completedAt.toLocaleDateString()}`);
      }

      if (stage.documents && stage.documents.length > 0) {
        doc.text('Documents:');
        stage.documents.forEach((docItem) => {
          doc.fontSize(11).text(`   ${docItem.label} (${docItem.uploadedAt.toLocaleDateString()})`);
        });
      }
      doc.moveDown(1);
    });

    // Diet Plan Summary (if available)
    if (data.dietPlan) {
      doc.addPage();
      doc.fontSize(16).text('Current Diet Plan', { underline: true });
      doc.moveDown(0.5);

      if (data.dietPlan.macros) {
        doc.fontSize(14).text('Macros:', { underline: true });
        if (typeof data.dietPlan.macros === 'object') {
          Object.entries(data.dietPlan.macros).forEach(([key, value]) => {
            doc.fontSize(12).text(`${key}: ${value}`);
          });
        } else {
          doc.fontSize(12).text(String(data.dietPlan.macros));
        }
        doc.moveDown(1);
      }

      if (data.dietPlan.weeklyPlan) {
        doc.fontSize(14).text('Weekly Plan Summary:', { underline: true });
        doc.fontSize(12).text(typeof data.dietPlan.weeklyPlan === 'object' 
          ? JSON.stringify(data.dietPlan.weeklyPlan, null, 2)
          : String(data.dietPlan.weeklyPlan));
      }
    }

    // Acknowledgements Summary
    if (data.acknowledgements && data.acknowledgements.length > 0) {
      doc.addPage();
      doc.fontSize(16).text('Task Acknowledgements', { underline: true });
      doc.moveDown(0.5);

      data.acknowledgements.forEach((ack) => {
        doc.fontSize(12).text(` ${ack.taskType} - ${ack.status} (${ack.createdAt.toLocaleDateString()})`);
      });
    }

    // Footer
    doc.moveDown(2);
    doc.fillColor('gray').fontSize(10).text('This is an automated consolidated report of your clinical journey.', {
      align: 'center'
    });
    doc.text('For questions, please contact your clinical team.', {
      align: 'center'
    });

    doc.end();
  });
}

client/src/components/RoleBasedSidebar.t.content:
import { Home, FileText, Stethoscope, Microscope, Apple, ChefHat, Truck, Users, Settings, Activity } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { Link, useLocation } from "wouter";
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
} from "@/components/ui/sidebar";

type MenuItem = {
  title: string;
  url: string;
  icon: React.ComponentType<{ className?: string }>;
};

export function RoleBasedSidebar() {
  const { user } = useAuth();
  const [location] = useLocation();

  const getMenuItems = (): MenuItem[] => {
    if (!user) return [];

    const role = user.role;

    switch (role) {
      case 'admin':
        return [
          { title: "Dashboard", url: "/admin", icon: Users },
          { title: "Staff Monitoring", url: "/admin#staff", icon: Activity },
          { title: "Settings", url: "/admin#settings", icon: Settings },
        ];

      case 'consultant':
        return [
          { title: "Customers", url: "/consultant", icon: Users },
          { title: "Upload Reports", url: "/consultant#upload", icon: FileText },
          { title: "Acknowledgements", url: "/consultant#acknowledgements", icon: Activity },
        ];

      case 'lab_technician':
        return [
          { title: "Test Queue", url: "/lab", icon: Microscope },
          { title: "Upload Results", url: "/lab#upload", icon: FileText },
          { title: "Acknowledgements", url: "/lab#acknowledgements", icon: Activity },
        ];

      case 'nutritionist':
        return [
          { title: "Customers", url: "/nutritionist", icon: Apple },
          { title: "Diet Charts", url: "/nutritionist#charts", icon: FileText },
          { title: "Acknowledgements", url: "/nutritionist#acknowledgements", icon: Activity },
        ];

      case 'chef':
        return [
          { title: "Active Plans", url: "/chef", icon: ChefHat },
          { title: "Preparation", url: "/chef#prepare", icon: Activity },
        ];

      case 'delivery':
        return [
          { title: "Deliveries", url: "/delivery-panel", icon: Truck },
          { title: "GPS Tracking", url: "/delivery-panel#gps", icon: Activity },
        ];

      case 'customer':
      default:
        return [
          { title: "Dashboard", url: "/dashboard", icon: Home },
          { title: "Profile", url: "/profile", icon: Settings },
        ];
    }
  };

  const menuItems = getMenuItems();
  const roleLabel = user?.role?.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase()) || 'User';

  return (
    <Sidebar data-testid="sidebar-role-based">
      <SidebarContent>
        <SidebarGroup>
          <SidebarGroupLabel>{roleLabel} Panel</SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              {menuItems.map((item) => (
                <SidebarMenuItem key={item.title}>
                  <SidebarMenuButton asChild isActive={location === item.url || location.startsWith(item.url + '#')}>
                    <Link href={item.url} data-testid={`link-${item.title.toLowerCase().replace(/\s+/g, '-')}`}>
                      <item.icon className="h-4 w-4" />
                      <span>{item.title}</span>
                    </Link>
                  </SidebarMenuButton>
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>
    </Sidebar>
  );
}

client/src/components/AuthenticatedLayou.content:
import { SidebarProvider, SidebarTrigger } from "@/components/ui/sidebar";
import { RoleBasedSidebar } from "@/components/RoleBasedSidebar";
import { Button } from "@/components/ui/button";
import { Moon, Sun } from "lucide-react";
import { useTheme } from "@/components/ThemeProvider";

type AuthenticatedLayoutProps = {
  children: React.ReactNode;
};

export function AuthenticatedLayout({ children }: AuthenticatedLayoutProps) {
  const { theme, setTheme } = useTheme();

  const style = {
    "--sidebar-width": "16rem",
    "--sidebar-width-icon": "3rem",
  };

  return (
    <SidebarProvider style={style as React.CSSProperties}>
      <div className="flex h-screen w-full">
        <RoleBasedSidebar />
        <div className="flex flex-col flex-1">
          <header className="flex items-center justify-between p-2 border-b">
            <SidebarTrigger data-testid="button-sidebar-toggle" />
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setTheme(theme === 'light' ? 'dark' : 'light')}
              data-testid="button-theme-toggle"
            >
              {theme === 'light' ? <Moon className="h-5 w-5" /> : <Sun className="h-5 w-5" />}
            </Button>
          </header>
          <main className="flex-1 overflow-auto">
            {children}
          </main>
        </div>
      </div>
    </SidebarProvider>
  );
}
